	$(function() {
	var defaultPanel = parseInt(getParam('panel'));
//Accordion
if (defaultPanel > 0) {
		$("#accordion-1,#accordion-2,#accordion-3,#accordion-4,#accordion-5,#accordion-6").accordion({
			collapsible: true,
			active: defaultPanel,
			heightStyle: "content",
			autoHeight: false
		});
        }
        else {
        $("#accordion-1,#accordion-2,#accordion-3,#accordion-4,#accordion-5,#accordion-6").accordion({
			collapsible: true,
			active: false,
			heightStyle: "content",
			autoHeight: false
		});
        }
	});
function getParam(name) { 
    var query = location.search.substring(1); 
    if (query.length) { 
        var parts = query.split('&'); 
        for (var i = 0; i < parts.length; i++) { 
            var pos = parts[i].indexOf('='); 
            if (parts[i].substring(0,pos) == name) { 
                return parts[i].substring(pos+1); 
            } 
        } 
    } return 0; 
}

