link.script:

    $(function() {
    	CreativeTemplate.Init();
    });

	var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": true,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,
        "ShowExtraDropdown": true,
        "HeaderIsSticky": false,

		// METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.DynamicStyleSheet();
			this.MyStart();
			this.SchoolList();
			this.Translate();
            this.ExtraDropdown();
            this.Header();
            this.Search();
            this.ChannelBar();
            this.StickyHeader();
            this.Slideshow();
            this.Body();
            this.ModEvents();
            this.GlobalIcons();
            this.AppAccordion();
            this.Footer();
            this.SocialIcons();
            this.JsMediaQueries();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
        	if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
			$("div.ui-widget.app.navigation li .bullet").each(function() {
            	$(this).insertAfter($(this).siblings("a"));
            });

            $("#hp-content .ui-widget.app .cs-article-slider .ui-articles li").each(function(k,v){
              	var link = $(".ui-article-title a",v).attr("href");
                if($(".ui-article-thumb .img",v).length){
                $(".ui-article-thumb .img",v).wrap('')
                }
              });

        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.StickyHeader();
        },

        "WindowScroll": function() {
            this.StickyHeader();
        },

        "JsMediaQueries": function() {
            // FOR SCOPE
            var _this = this;

        	var windowWidth = $(window).width();

            switch(this.GetBreakPoint()) {
                case "desktop": 
                    if(windowWidth <= 1024) {
                    	$("nav").appendTo($("#gb-header-outer > div"));
                    } else {
                    	$("nav").appendTo($(".gb-header.right"));
                    }

                    $('[data-region="c"] .app:eq(0) .ui-widget-detail > p').appendTo($('[data-region="c"] .app:eq(0) .ui-widget-header'));

                    $("#gb-social-media-icons").appendTo($(".gb-footer.two"));
                break;
                case "768":
                case "640":
                case "480":
                case "320":
                    $("nav").appendTo($("#gb-header-outer > div"));
                    $("header").removeClass("sticky").removeAttr("style");
                    $("#gb-sticky-logo").addClass("hidden").find("a").attr({"tabindex": -1, "aria-hidden": true});
                    $("#gb-logo").removeClass("hidden").find("a").attr({"tabindex": 0, "aria-hidden": false});
                    $("nav .gb-search-btn").addClass("hidden").attr({"tabindex": -1, "aria-hidden": true});
                    $("#gb-mystart .gb-search-btn").removeClass("hidden").attr({"tabindex": 0, "aria-hidden": false});

                    this.HeaderIsSticky = false;

                    $('[data-region="c"] .app:eq(0) .ui-widget-header p').prependTo($('[data-region="c"] .app:eq(0) .ui-widget-detail'));

                    $("#gb-social-media-icons").appendTo($(".gb-footer.one"));
                break;
            }
        },

        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



			// SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
				userOptionsItems += "
" + $(".sw-mystart-button.signin").html() + "";
			}

			// REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



            // ADD DISTRICT HOME
            if(this.ShowDistrictHome && $(".sw-mystart-button.home").length) {
            	$(".sw-mystart-button.home > a > span").text("District Home");
            	$("#gb-mystart").prepend($(".sw-mystart-button.home"));
            }

			// ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

			// BIND DROPDOWN EVENTS
			this.DropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

		"SchoolList": function() {
            // FOR SCOPE
            var template = this;

			// ADD SCHOOL LIST
			if(this.ShowSchoolList) {
               $.schoolListMinibase({
                    'miniBaseDomainID': 1,
                    'miniBaseModuleInstanceID': 48404,
                    'cache': true,
                    'schoolListReady': function(schoolList) {
                        /*
                        The returned value is an array of objects and each object is a school record.
                        The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                        */

                        var schools = '', sortedSchools = {};

                        // SORT THE SCHOOL LIST TO TRY TO GET THE EXPECTED TYPES IN THE ORDER WE WANT
                        var expectedNameOrder = ["elementary", "middle", "high", "specialty campuses"];
                        schoolList.sort(function(a, b) {
                            // WE WANT THE SPECIFIC CATEGORIES ABOVE TO COME FIRST AND IN THE SPECIFIED ORDER
                            // WE'LL CAPTURE THOSE IN THIS FIRST CHECK
                            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > -1 && expectedNameOrder.indexOf(b.schoolType.toLowerCase()) > -1) {
                                if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                    return -1;
                                }
                                if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                    return 1;
                                }
                            }

                            // HERE, THE SCHOOL CATEGORY DOESN'T EXIST IN THE CATEGORY ARRAY ABOVE
                            else {
                                if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                    return -1;
                                }
                                if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                    return 1;
                                }
                            }

                            // NEITHER CHECK APPLIES SO WE JUST LEAVE THIS IN IT'S CURRENT POSITION
                            return 0;
                        });

                      	// SORT THE SCHOOLS INTO TYPE OBJECTS
                        for(var type in schoolList) {
                            if(schoolList[type].schoolVisible.toLowerCase() == 'true') {
                                var thisType = schoolList[type].schoolType.toLowerCase();

                                // CHECK FOR MULTIPLE COMMA SEPARATED VALUES
                                if(schoolList[type].schoolType.indexOf(",") > -1) {
                                    var subTypes = schoolList[type].schoolType.split(",");

                                    // LOOP THROUGH THE SUB TYPES
                                    for(var subType in subTypes) {
                                        var thisSubType = $.trim(subTypes[subType]).toLowerCase();

                                        // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                                        if(!sortedSchools.hasOwnProperty(thisSubType)) {
                                            // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                            sortedSchools[thisSubType] = [schoolList[type]];
                                        } else {
                                            // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                            sortedSchools[thisSubType].push(schoolList[type]);
                                        }
                                    }
                                }

                                // SINGLE TYPE VALUE
                                else {
                                    // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                                    if(!sortedSchools.hasOwnProperty(thisType)) {
                                        // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                        sortedSchools[thisType] = [schoolList[type]];
                                    } else {
                                        // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                        sortedSchools[thisType].push(schoolList[type]);
                                    }
                                }
                            }
                        }

                        // BUILD THE SCHOOL MENU STRUCTURE
                        var schoolMenuLists = '';
                        var schoolNumCounter = 1;
                        $.each(sortedSchools, function(type, schools) {
                            var typeName = type;

                            // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES
                            schools.sort(function(a, b) {
                                if(a.schoolName > b.schoolName) {
                                    return 1;
                                }
                                if(a.schoolName < b.schoolName) {
                                    return -1;
                                }
                                return 0;
                            });

                            // START THE CATEGORY CONTAINER
                            schoolMenuLists += '
';

                            // BUILD HEADER
                            schoolMenuLists += '
' + type + '';

                            // BUILD LIST
                            schoolMenuLists += '';
                            for(var school in schools) {
                                schoolMenuLists += '
' + schools[school].schoolName + '';

                                schoolNumCounter++;
                            }

                            // CLOSE EVERYTHIGN UP
                            schoolMenuLists += '';
                        });

                        // ADD THE COMPLETED MENU TO THE DOM
                        $("#gb-school-list-inner").html(schoolMenuLists);

                        //SPLIT ELEMENTARY INTO TWO COLUMNS
                        var totalItems =  Math.round($(".gb-school-list-links.elementary li").length/2);

                        $(".gb-school-list-links.elementary").before('');
                        $(".gb-school-list-links.elementary li:nth-child(-n+"+totalItems+")").appendTo($(".gb-school-list-links.elementary").eq(0));
                        $(".gb-school-list-links.elementary").wrapAll('
');

                        // BIND THE TAB PANEL EVENTS
                        template.SchoolMenuControls();

                        // BUILD THE RESPONSIVE MENUS NOW THAT WE HAVE THE SCHOOLS
                        template.RsMenu(sortedSchools);
                    }
                });
			}
		},

        "SchoolMenuControls": function() {
        	// FOR SCOPE
            var template = this;

        	// SCHOOLS BUTTON CLICK/KEYDOWN
            $(".cs-mystart-button.schools button").on("click keydown", function(e) {
                if(template.AllyClick(e)) {
                    e.preventDefault();

                    // MENU IS NOT OPEN
                    if(!$(this).parent().hasClass("open")) {
                        // OPEN THE MENU
                        $("#gb-school-list-outer").fadeIn().attr("aria-hidden", "false");
                        if(e.type == "keydown") {
                        	$("#gb-school-list .gb-school-list-links.elementary-schools > li:first-child a").focus();
                        }

                        // SET ATTRIBUTES
                        $("body").addClass("school-list-overflow");
                        $(this).parent().addClass("open").find("button").attr("aria-expanded", "true");

                    } else if($(this).parent().hasClass("open")) {
                        // CLOSE THE MENU
                        $("#gb-school-list-outer").fadeOut(function() {
                            // SET ATTRIBUTES
                            $("body").removeClass("school-list-overflow");
                        }).attr("aria-hidden", "true");

                        // SET ATTRIBUTES
                        $(this).parent().removeClass("open").find("button").attr("aria-expanded", "false");
                    }
                }
            });

            // SHIFT+TAB FROM FIRST LINK
            $(document).on("keydown", "#gb-school-list .gb-school-list-links.elementary-schools > li:first-child a", function(e) {
            	if(e.keyCode == template.KeyCodes.tab && e.shiftKey) {
                    e.preventDefault();

                    $("#gb-close-school-menu").focus();
                }
            });

            // TAB FROM CLOSE BUTTON
            $(document).on("keydown", "#gb-close-school-menu", function(e) {
            	if(e.keyCode == template.KeyCodes.tab && !e.shiftKey) {
                    e.preventDefault();

                    $("#gb-school-list .gb-school-list-links.elementary-schools > li:first-child a").focus();
                }
            });

            // LINK ARROW CONTROLS
            $(document).on("keydown", ".gb-school-list-links a", function(e) {
            	var thisNum = parseInt($(this).attr("data-school-num"));
                var totalNum = $(".gb-school-list-links a").length;

            	switch(e.keyCode) {
                	case template.KeyCodes.up:
                    case template.KeyCodes.left:
                    	e.preventDefault();

						if(thisNum == 1) {
                        	$("#gb-close-school-menu").focus();
                        } else {
                        	$(".gb-school-list-links a[data-school-num='" + (thisNum - 1) + "']").focus();
                        }
                    break;

                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                    	e.preventDefault();

                        if(thisNum == totalNum) {
                        	$("#gb-close-school-menu").focus();
                        } else {
                        	$(".gb-school-list-links a[data-school-num='" + (thisNum + 1) + "']").focus();
                        }
                    break;
                }
            });

            // CLOSE MENU
            $(document).on("click keydown", "#gb-close-school-menu", function(e) {
            	if(template.AllyClick(e)) {
                	e.preventDefault();

                	// CLOSE THE MENU
                    $("#gb-school-list-outer").fadeOut(function() {
                        // SET ATTRIBUTES
                        $("body").removeClass("school-list-overflow");
                    }).attr("aria-hidden", "true");

                    // SET ATTRIBUTES
                    $(".cs-mystart-button.schools").removeClass("open").find("button").attr("aria-expanded", "false").focus();
                }
            });

            // CLOSE MENU WITH ESC
            $(document).on("keydown", "#gb-school-list-outer", function(e) {
                if(e.keyCode == template.KeyCodes.esc) {
                    e.preventDefault();

                    // CLOSE THE MENU
                    $("#gb-school-list-outer").fadeOut(function() {
                        // SET ATTRIBUTES
                        $("body").removeClass("school-list-overflow");
                    }).attr("aria-hidden", "true");

                    // SET ATTRIBUTES
                    $(".cs-mystart-button.schools").removeClass("open").find("button").attr("aria-expanded", "false").focus();
                }
            });
        },

