		"Header": function() {
            // ADD HEADER LOGO
            var logoSrc = jQuery.trim("/cms/lib/TX21000533/Centricity/Template/GlobalAssets/images///logos/district-logo.png");
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append("");
			} else {
				$("#gb-logo").append("");
			}

            // ADD STICKY HEADER LOGO
            var logoSrc2 = jQuery.trim('/cms/lib/TX21000533/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg');
			var srcSplit2 = logoSrc2.split("/");
			var srcSplitLen2 = srcSplit2.length;
			if((logoSrc2 != "") && (srcSplit2[srcSplitLen2 - 1] != "default-man.jpg")) {
				$("#gb-sticky-logo").append("");
			} else {
				$("#gb-sticky-logo").append("");
			}           
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('li');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-item").each(function(){
            	if($(".sw-channel-dropdown li", this).length == 0) {
                	$(this).addClass("no-dropdown");
                }
            });
        },

        "StickyHeader": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = $("main").offset().top;
                        var windowWidth = $(window).width();

                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.HeaderIsSticky) {
                                $("header").removeClass("sticky").removeAttr("style");
                                $("#gb-sticky-logo").addClass("hidden").find("a").attr({"tabindex": -1, "aria-hidden": true});
                                $("#gb-logo").removeClass("hidden").find("a").attr({"tabindex": 0, "aria-hidden": false});
                                $("nav .gb-search-btn").addClass("hidden").attr({"tabindex": -1, "aria-hidden": true});
                                $("#gb-mystart .gb-search-btn").removeClass("hidden").attr({"tabindex": 0, "aria-hidden": false});

                                this.HeaderIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                        	$("header").css("height", $("header").outerHeight(true));

                            if(!this.HeaderIsSticky) {
                                $("header").addClass("sticky");
                                $("#gb-logo").addClass("hidden").find("a").attr({"tabindex": -1, "aria-hidden": true});
                                $("#gb-sticky-logo").removeClass("hidden").find("a").attr({"tabindex": 0, "aria-hidden": false});
                                $("#gb-mystart .gb-search-btn").addClass("hidden").attr({"tabindex": -1, "aria-hidden": true});
                                $("nav .gb-search-btn").removeClass("hidden").attr({"tabindex": 0, "aria-hidden": false});

                                this.HeaderIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("header").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-header-wrapper").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.HeaderIsSticky) {
                            $("header").removeClass("sticky").removeAttr("style");

                            this.HeaderIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

