        "Slideshow": function() {
            // FOR SCOPE
            var _this = this;

            if($("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                this.MMGPlugin();
            }

            // MMG PLACEHOLDER
            if($(".sw-special-mode-bar").length && !$("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                $.csMultimediaGalleryDefault({
                    "contentRegion" : "#sw-content-container10.ui-hp",
                    "defaultImageSrc" : "/cms/lib/TX21000533/Centricity/Template/9/defaults/mmg-photo.png",
                    "allLoaded": function() {
                        _this.MMGPlugin();
                    }
                });
            }
        },

        "MMGPlugin": function() {
            // FOR SCOPE
            var _this = this;

            var mmg = eval("multimediaGallery" + $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
            mmg.props.defaultGallery = false;

            $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                "efficientLoad" : true,
                "imageWidth" : 1480,
                "imageHeight" : 565,
                "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                "linkedElement" : ["title"], // ["image", "title", "overlay"]
                "playPauseControl" : true,
                "backNextControls" : true,
                "bullets" : true,
                "thumbnails" : false,
                "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "autoRotate" : true,
                "hoverPause" : true,
                "transitionType" : "fade",
                "transitionSpeed" : 2,
                "transitionDelay" : 6,
                "fullScreenRotator" : false,
                "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                "allLoaded" : function(props) {
                    $(".mmg-control.back", props.element).before($(".mmg-control.next", props.element));
                    $(".mmg-control").wrapInner("");

                    $(".mmg-description a").focus(function() {
                       $(this).parents("li").addClass("focused");
                    });

                    $(".mmg-description a").blur(function() {
                        $(this).parents("li").removeClass("focused");                  
                    });
                }, // props.element, props.mmgRecords
            });
        },

        "ModEvents": function() {
            $(".hp .ui-widget.app.upcomingevents").modEvents({
				columns     : "no",
				monthLong   : "yes",
				dayWeek     : "yes"
			});

            $(".sp .ui-widget.app.upcomingevents").modEvents({
				columns     : "yes",
				monthLong   : "yes",
				dayWeek     : "yes"
			});

			$(".ui-widget.app.upcomingevents .ui-article").each(function() {
				if (!$("h1.ui-article-title.sw-calendar-block-date", this).length){
					var moveArticle = $(this).html();
					$(this).parent().prev("li").find(".ui-article").append(moveArticle);
					$(this).parent().remove();
				}
			});

            $(".hp .ui-widget.app.upcomingevents .ui-article-description").each(function() {    
                $(this).prev().children(".joel-day, .joel-month").wrapAll('');
            	if($(".sw-calendar-block-time", this).length) {
                    $(".sw-calendar-block-time", this).html($(".sw-calendar-block-time", this).text().replace(/ AM/g, "am"));
                	$(".sw-calendar-block-time", this).html($(".sw-calendar-block-time", this).text().replace(/ PM/g, "pm"));
                    $(".sw-calendar-block-time", this).html($(".sw-calendar-block-time", this).text().replace(/ - /g, "-"));
                } else {
					$(this).prepend('All Day');
                }
            });

            $(".sp .ui-widget.app.upcomingevents .upcoming-column-container").each(function() {    
                $(this).find(".joel-day, .joel-month").wrapAll('');
            	if($(".sw-calendar-block-time", this).length) {
                    $(".sw-calendar-block-time", this).html($(".sw-calendar-block-time", this).text().replace(/ AM/g, "am"));
                	$(".sw-calendar-block-time", this).html($(".sw-calendar-block-time", this).text().replace(/ PM/g, "pm"));
                    $(".sw-calendar-block-time", this).html($(".sw-calendar-block-time", this).text().replace(/ - /g, "-"));
                } else {
					$(".upcoming-column.right", this).prepend('All Day');
                }
            });

            $(".hp .upcomingevents .sw-calendar-block-title a").focus(function() {
                $(this).parents("li").addClass("focused");
            });

            $(".hp .upcomingevents .sw-calendar-block-title a").blur(function() {
              	$(this).parents("li").removeClass("focused");                  
            });
        },

