        "Search": function() {
        	// FOR SCOPE
            var _this = this;

            // OPEN SEARCH DIALOG
            $(document).on("click keydown", ".gb-search-btn", function(e) {
                if(_this.AllyClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr({
                        	"aria-expanded": "true"
                        });
                        $("#gb-search-outer").attr("aria-hidden", "false").slideDown(250, "linear");
                        $("#gb-page").addClass("search-open");

                        setTimeout(function() {
                            $("#gb-search-input").focus();
                        }, 260);
                    } else {
                    	$(this).attr({
                        	"aria-expanded": "false"
                        });
                        $("#gb-search-outer").attr("aria-hidden", "true").slideUp(250, "linear");
                        $("#gb-page").removeClass("search-open");
                    }
                }
            });

			$("#gb-search-form").submit(function(e){
				e.preventDefault();

				if($("#gb-search-input").val() != "I'M LOOKING FOR...") {
					window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                }
			});

            $(document).on("keydown", "#gb-search-submit-btn", function(e) {
                if(e.keyCode == _this.KeyCodes.tab) {
                	e.preventDefault();
                    $(".gb-search-btn").focus();
                }
            });

			$("#gb-search-input").focus(function() {
				if($(this).val() == "I'M LOOKING FOR...") {
					$(this).val("");
				}
			}).blur(function() {
				if($(this).val() == "") {
					$(this).val("I'M LOOKING FOR...");
				}
			});
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown") {
                if(event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter) {
                    return true;
                }
            } else {
                return false;
            }
        },

        "HextoRGBA": function(hex, opacity){
            var opacity = isNaN(opacity) ? 100 : opacity;
            var hex = hex.replace('#', '');
            if(hex.length === 6) {
              var r = parseInt(hex.substring(0, 2), 16);
              var g = parseInt(hex.substring(2, 4), 16);
              var b = parseInt(hex.substring(4, 6), 16);
            } else {
              var rd = hex.substring(0, 1) + hex.substring(0, 1);
              var gd = hex.substring(1, 2) + hex.substring(1, 2);
              var bd = hex.substring(2, 3) + hex.substring(2, 3);
              var r = parseInt(rd, 16);
              var g = parseInt(gd, 16);
              var b = parseInt(bd, 16);
            }

            return 'rgba(' + r + ', ' + g + ', ' + b + ', ' + opacity / 100 + ')';
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

