                        ["Bulgarian", "български", "bg"],
                        ["Burmese", "မြန်မာ", "my"],
                        ["Catalan", "català", "ca"],
                        ["Cebuano", "Cebuano", "ceb"],
                        ["Chichewa", "Chichewa", "ny"],
                        ["Chinese Simplified", "简体中文", "zh-CN"],
                        ["Chinese Traditional", "中國傳統的", "zh-TW"],
                        ["Corsican", "Corsu", "co"],
                        ["Croatian", "hrvatski", "hr"],
                        ["Czech", "čeština", "cs"],
                        ["Danish", "dansk", "da"],
                        ["Dutch", "Nederlands", "nl"],
                        ["Esperanto", "esperanto", "eo"],
