                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADD SPAN TAGS TO EACH LINK WITHIN SITE SHORTCUTS APPS
            $(".siteshortcuts a").wrapInner("");

            //MOVE SHORTCUTS FOLLOW INTO FOOTER
            $(".siteshortcuts").each(function(){
            	$(this).find(".app-level-social-follow").appendTo($(this).find(".ui-widget-footer"));
