                    "viewerNum": [3, 1, 1, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "transitionSpeed": .5 // SECONDS
                });

                $("[data-region='b'] .ui-widget.app.upcomingevents, [data-region='c'] .ui-widget.app.upcomingevents, [data-region='d'] .ui-widget.app.upcomingevents, [data-region='h'] .ui-widget.app.upcomingevents").csArticleSlider({
                    "viewerNum": [4, 2, 2, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "transitionSpeed": .5 // SECONDS
                });

                //MOVE APP ITEMS INTO HEADER
