                    $(this).find(".cs-article-slider-control.back").addClass("cs-slider-nav-item").appendTo($(this).parent().prev());
                    //READ MORE
                    $(this).parent().find(".ui-read-more").addClass("cs-slider-nav-item more-wrap").appendTo($(this).parent().prev());
                    //RSS
                    $(this).parent().next().find(".app-level-social-rss").addClass("cs-slider-nav-item more-wrap").appendTo($(this).parent().prev());
