                    $(this).find(".cs-article-slider-control.next").addClass("cs-slider-nav-item").appendTo($(this).parent().prev());

                    //WRAP ALL IN A CONTAINER
                    $(this).parent().prev().find(".cs-slider-nav-item").wrapAll("

