                    }
                })
            })
        },

        "ArticleSliderExtend": function(config, element, ArticleSlider) {
            $(ArticleSlider.article + " a:not(.ui-article-thumb)", element).attr("tabindex", "0");
            $(ArticleSlider.article, element).attr("aria-hidden", "false");

            ArticleSlider.SetProperties = function() {
                // SET ARTICLE NUMBER PROPERTY
                this.articleNum = $(this.article + ":not(.clone)", element).length;

                // SET ARTICLE WIDTH PROPERTY
                this.articleWidth = $(this.article + ":first-child", element).outerWidth(true);
            }

            // SET ORIGINAL BuildStructure METHOD
            var BuildStructure = ArticleSlider.BuildStructure;

            // REDEFINE BuildStructure METHOD
            ArticleSlider.BuildStructure = function() {
                // CALL ORIGINAL METHOD
                BuildStructure.call(this);

                // CLONE ENTIRE ARTICLE SET
                var clonedArticles = $(".ui-articles > li", element).clone();
                $.each(clonedArticles, function(index, article) {
                    $(article).addClass("clone");
                });
                $(".ui-articles", element).append(clonedArticles);
                $(".ui-articles", element).prepend($(".ui-articles > li:last-child", element));

                // UPDATE articleWidth PROPERTY TO MAKE SURE IT IS ACCURATE
                this.articleWidth = $(this.article + ":first-child", element).outerWidth(true);

                var breakPoint = this.GetBreakPoint();
                if(this.articleNum > breakPoint.viewNum) {
                    $(".ui-articles > li.clone", element).show();
                    $(this.slider, element).css("left", "-" + this.articleWidth + "px");
                    this.sliderPosition = this.sliderPosition - this.articleWidth;
                } else {
                    $(".ui-articles > li.clone", element).hide();
                    $(this.slider, element).css("left", "0px");
                }
            }

            // SET ORIGINAL WindowResize METHOD
            var WindowResize = ArticleSlider.WindowResize;

            // REDEFINE WindowResize METHOD
            ArticleSlider.WindowResize = function() {
                // CALL ORIGINAL METHOD
                WindowResize.call(this);

                var breakPoint = this.GetBreakPoint();

                if(this.articleNum > breakPoint.viewNum) {
                    $(".ui-articles > li.clone", element).show();
                    $(this.slider, element).css("left", "-" + this.articleWidth + "px");
                    this.sliderPosition = this.articleWidth * -1;
                } else {
                    $(".ui-articles > li.clone", element).hide();
                    $(this.slider, element).css("left", "0px");
                }
            }

            var AnimateSlider = ArticleSlider.AnimateSlider;

            ArticleSlider.AnimateSlider = function(props) {
                $(this.article + " a:not(.ui-article-thumb)", element).attr("tabindex", "-1");

                var breakPoint = this.GetBreakPoint();
                if(props.direction == "back") {
                    $(this.article + ":nth-of-type(n+" + (breakPoint.viewNum + 2) + ")", element).attr("aria-hidden", "true");
                    $(this.article + ":nth-of-type(n+" + (breakPoint.viewNum + 2) + ") a:not(.ui-article-thumb)", element).attr("tabindex", "-1");
                } else {
                    $(this.article + ":nth-of-type(n+" + (breakPoint.viewNum + 3) + ")", element).attr("aria-hidden", "true");
                    $(this.article + ":nth-of-type(n+" + (breakPoint.viewNum + 3) + ") a:not(.ui-article-thumb)", element).attr("tabindex", "-1");
                }

                AnimateSlider.call(this, props);
            }

            ArticleSlider.UpdateAttributes = function(props) {
                var breakPoint = this.GetBreakPoint();

                // UPDATE ATTRIBUTES
                $(this.article + " a:not(.ui-article-thumb)", element).attr("tabindex", "0");
                $(this.article + ":first-child", element).attr("aria-hidden", "true");
                $(this.article + ":first-child a:not(.ui-article-thumb)", element).attr("tabindex", "-1");
                $(this.article + ":nth-of-type(n+" + (breakPoint.viewNum + 2) + ")", element).attr("aria-hidden", "true");
                $(this.article + ":nth-of-type(n+" + (breakPoint.viewNum + 2) + ") a:not(.ui-article-thumb)", element).attr("tabindex", "-1");

                // UPDATE NOTIFICATION
                if(props.updateNotification) {
                    var articleIndexes = '';
                    $(this.article + "[aria-hidden='false']", element).each(function(i) {
                        if(i != 0) {
                            if(i == breakPoint.viewNum - 1) {
                                articleIndexes += ", and ";
                            } else {
                                articleIndexes += ", ";
                            }
                        }

                        articleIndexes += parseInt($(this).attr("data-index")) + 1;
                    });

                    $(".cs-article-slider-notifications", element).text("Articles " + articleIndexes + " are currently in view.");
                }
            }
        },

        "Footer": function() {
            // FOR SCOPE
            var _this = this;

            //FOOTER BACKGROUND IMAGE
            var footerBackgroundImageToggle = true;
            if(footerBackgroundImageToggle == true){
                var footerBackgroundSrc = jQuery.trim('/cms/lib/TX50000567/Centricity/Template/GlobalAssets/images///BG Images/campus aerial photo.jpg');
                var footerBackgroundSrcSplit = footerBackgroundSrc.split("/");
                var footerBackgroundSrcSplitLength = footerBackgroundSrcSplit.length;

                if((footerBackgroundSrc == "") || (footerBackgroundSrcSplit[footerBackgroundSrcSplitLength - 1] == "default-man.jpg")){ //USE DEFAULT BACKGROUND IMAGE
                    $(".footer-row.one.desktop").css("background-image","url('/cms/lib/TX50000567/Centricity/Template/21/defaults/footer-bg.jpg')");
                } else {
                    $(".footer-row.one.desktop").css("background-image","url('"+footerBackgroundSrc+"')");
                } 
            } else {
                $(".footer-row.one").addClass("no-bg-img");
            }

            // MOVE Bb FOOTER STUFF
            $(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-schoolwires-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-schoolwires-footer.copyright").append($("#sw-footer-copyright").html());

            //SCROLL TO TOP
            $(".to-top").on("click keydown", function(e) {
                e.preventDefault();
                if(_this.AllyClick(e)) {
                        $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                            $("li.sw-channel-item:first-child > a").focus();
                        });
                }
            });

            //CHECK FOR FOOTER LINKS
            var footerLinksToggles = [true, true, true, true, false, false]
            var numFooterColumns = 2;

            if(true == false && false == false && false == false){
            	$(".gb-school-information").addClass("single-footer-link-column");
            }

            for(i = 0; i < footerLinksToggles.length; i++){
                if(footerLinksToggles[i] == true){
                	$(".gb-school-information").show();
                    numFooterColumns = 3;
                }
            }

            if(numFooterColumns == 2){
            	$(".gb-school-information").addClass("two-columns").show();
            } 
        },

        "Slideshow": function() {
            // FOR SCOPE
            var _this = this;

            if("Streaming Video" == "Carousel Multimedia Gallery" || "Streaming Video" == "Full Screen Multimedia Gallery") {
            	if($("#sw-content-container10 .ui-widget.app.multimedia-gallery").length) {
                    this.MMGPlugin();
                }

                // MMG PLACEHOLDER
				if($(".ui-hp").length && !$("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                    $.csMultimediaGalleryDefault({
                        "contentRegion" : "#sw-content-container10.ui-hp",
                        "defaultImageSrc" : "/cms/lib/TX50000567/Centricity/Template/21/defaults/summit-carousel-2.jpg",
                        "allLoaded": function() {
                            _this.MMGPlugin();
                        }
                    });
                }
            }

            //SCROLL DOWN
            $("#scroll-down").on('click keypress', function(e){
                    if(_this.AllyClick(e) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                        $('html, body').animate({
                            scrollTop: $(this).offset().top + 65
                    }, 500);
                }
            });
        },

        "MMGPlugin": function() {
            // FOR SCOPE
            var _this = this;

            if("Streaming Video" == "Carousel Multimedia Gallery"){
                var imgWidth = 803;
                var imgHeight = 490;
                var transitionType = "slide";
                var fullScreenRotator = false;
            }
            if("Streaming Video" == "Full Screen Multimedia Gallery"){
                var imgWidth = 1500;
                var imgHeight = 670;
                var transitionType = "fade";
                var fullScreenRotator = false;
            }

            var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
            mmg.props.defaultGallery = false;

            $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                "efficientLoad" : true,
                "imageWidth" : imgWidth,
                "imageHeight" : imgHeight,
                "mobileDescriptionContainer": [960, 768, 640, 480, 320], // [960, 768, 640, 480, 320]
                "galleryOverlay" : false,
                "linkedElement" : [],  // ["image", "title", "overlay"]
                "playPauseControl" : true,
                "backNextControls" : true,
                "bullets" : false,
                "thumbnails" : false,
                "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "autoRotate" : true,
                "hoverPause" : true,
                "transitionType" : transitionType,
                "transitionSpeed" : 2,
                "transitionDelay" : 6,
                "fullScreenRotator" : fullScreenRotator,
                "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                "onTransitionStart" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                "allLoaded" : function(props) {
                    //WRAP CONTROLS
                    $(".mmg-control").wrapAll("
");
                    $(".mmg-control.back").insertBefore(".mmg-control.play-pause");

                    if("Streaming Video" == "Carousel Multimedia Gallery"){
                        var vw = $(window).width();
                        var leftMargin = .091 * vw;
                        if(vw >= 1300){
                            var descWidth = vw - (leftMargin + 803);
                            if($("#dashboard-sidebar").length){
                                descWidth = descWidth - 50;
                            }
                        } else {
                            var descWidth = vw - $(".mmg-slide.active").width();
                            if($("#dashboard-sidebar").length){
                                descWidth = descWidth - 19;
                            }
                        }



                        $(".mmg-description-outer").width(descWidth);
                    } else {
                        $(".cs-mmg-control-wrapper").appendTo(".mmg-description-outer");
                        $("").insertAfter(".mmg-description-outer");
                    }

                }, // props.element, props.mmgRecords
                "onWindowResize": function(props) {
                    if(fullScreenRotator == false){
                        var vw = $(window).width();
                        var leftMargin = .091 * vw;
                        if(vw >= 1300){
                            var descWidth = vw - (leftMargin + 803);
                            if($("#dashboard-sidebar").length){
                                descWidth = descWidth - 50;
                            }
                        } else {
                            var descWidth = vw - $(".mmg-slide.active").width();
                            if($("#dashboard-sidebar").length){
                                descWidth = descWidth - 19;
                            }
                        }

                        $(".mmg-description-outer").width(descWidth);
                    }
                } // props.element, props.mmgRecords
            });  
        },

