                "accordionBreakpoints" : [640, 480, 320]
            });
        },

        "Search": function() {
            // FOR SCOPE
            var _this = this;

            //OPENING SEARCH
            $("#gb-search-control").on('click keypress', function(event){
                var searchControl = this;

                if(_this.AllyClick(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                    if(!$(this).hasClass("open")){
                        $(searchControl).addClass("open").attr("aria-expanded","true");
                        $("#gb-search-form-wrapper").addClass("open").attr("aria-hidden","false");
                        $("#gb-search-input").focus().attr("tabindex","0");
                    } else {
                        $(searchControl).removeClass("open").attr("aria-expanded","false");
                        $("#gb-search-form-wrapper").removeClass("open").attr("aria-hidden","true");
                        $("#gb-search-input").attr("tabindex","-1");
                    }
                }
            })

            //SEARCH FORM SUBMISSION
            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "Search..." && $.trim($("#gb-search-input").val()) != "") {
                    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });

            //SEARCH INPUT TEXT
            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            });

            $("#gb-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });

            //SEARCH KEYBOARD NAV
            $("#gb-search-input").on("keydown", function(e){
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                	case _this.KeyCodes.tab:
                        if(!e.shiftKey){
                        	e.preventDefault(); 
                            $("#gb-search-control").focus();
                        }
                    break;
                }
            });

            $("#gb-search-control").on("keydown", function(e){
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                	case _this.KeyCodes.tab:
                        if(!e.shiftKey){

                        } else {
                        	if($(this).hasClass("open")){
                            	e.preventDefault(); 
                            	$("#gb-search-input").focus();
                            }
                        }
                    break;
                }
            });

            $("#gb-search-control").focusout(function(){
            	if($("#gb-search-control").hasClass("open")) {
                	setTimeout(function () {
                        if(!$("#gb-search-input").is(":focus")){
                            $("#gb-search-control").removeClass("open").attr("aria-expanded","false");
                            $("#gb-search-form-wrapper").removeClass("open").attr("aria-hidden","true");
                            $("#gb-search-input").attr("tabindex","-1");
                        }
                    }, 100);
                }
            });

            $("#gb-search-input").focusout(function(){
            	if($("#gb-search-control").hasClass("open")) {
                	setTimeout(function () {
                        if(!$("#gb-search-control").is(":focus")){
                            $("#gb-search-control").removeClass("open").attr("aria-expanded","false");
                            $("#gb-search-form-wrapper").removeClass("open").attr("aria-hidden","true");
                            $("#gb-search-input").attr("tabindex","-1");
                        }
                    }, 100);
                }
            });

        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown") {
                if(event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter) {
                    return true;
                }
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

