function GetRefresh_923(kc, id, sd, pb) {
    var sv = document.getElementById("selSearch_923").value;
    var formData = "kc=" + kc + "&id=" + id + "&sd=" + sd + "&pb=" + pb + "&sv=" + sv;
    $.ajax({
        type: 'POST',
        url: "/runtime/widgets/_CommPartial.aspx",
        data: formData,
        success: function (data) {
            $("#ContentPlaceHolder4_ContentRepeater4_ctl00_1_communicator_1").empty().html(data);
        }
    });
}

