var hsList = '';

var msList = '';

var esList = '';

var speList = '';

 if( $('.sw-mystart-dropdown.schoollist').length > 0 ){
 //add sort here if user doesn't think the list is sorted alphabetically
  $('.sw-mystart-dropdown.schoollist .sw-dropdown-list li').each(function(k, v){ 
   if($('a', v).text().search( /\sHS|\sHigh|\shigh|\shs/g) >= 0){
    hsList += '
' + $(v).html() + '';
   }else  if($('a', v).text().search( /\sMS|\sMiddle|\smiddle|\sms/g) >= 0){
    msList += '
' + $(v).html() + '';
   }else if($('a', v).text().search( /\sES|\sElem|\selem|\ses/g) >= 0){
    esList += '
' + $(v).html() + '';
   }else{
    speList += '
' + $(v).html() + '';
   }
  });
 $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html( hsList + msList + esList + speList);
 }
 /* End 2016-2-22 Jeremy Additions */
}); 

$(function(){
