	$(function() {
    	CreativeTemplate.Init(); //tip of the iceberg
    });


    var CreativeTemplate = {

    	/*****
        	PROPERTIES
        *****/

        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "DefaultLogoSrc": "/cms/lib/TX01918037/Centricity/Template/20/district-logo.png",
        "DropdownTimeout": "", //Will store the CustomDropdown timeout object that will get passed among the dropdown functions.
        "DynamicStyles": "", //This can be added to and if it's not empty it'll be added to the page in a stylesheet
        "MobileMenuBreakpoint": 768,
        "csNavIsSticky": false,



        /*****
        	METHODS
        *****/

// BIG BANG
        "Init": function() {
        	$('[data-hide=""], [data-hide="!false"], [data-hide="true"]').remove();

            this.PasswordResetPage();

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.SchoolTabList();
            this.RsMenu();
            this.MyStart();
            this.Search();
            this.Header();
            this.CustomDropdowns();
            this.ChannelBar();
            this.StickyHeader();
            this.GlobalIcons();
            this.Body();
            this.StreamingVideo();
            this.Footer();
            this.FindWrappingLinks('#gb-footer .footer-bottom li', 175);
            this.AppAccordion();
            csGlobalJs.OpenInNewWindowWarning();

            if( this.DynamicStyles != "" ){ //Build out our stylesheet if any of the above items have added styles to it.
            	this.BuildStyleSheet();
            }

            $(window)
            	.load(function(){ 
                	CreativeTemplate.WindowLoad(); 
				})
            	.resize(function(){ 
                     CreativeTemplate.WindowResize(); 
                     CreativeTemplate.CalendarListView();
                })
                .scroll(function(){
                    CreativeTemplate.WindowScroll();
                });
        },


// TOP LEVEL (Window/Document) EVENT HANDLERS
        "WindowLoad": function() {
            this.CalendarListView();
            csGlobalJs.OpenInNewWindowWarning();

              // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
            $('.navigation.app .bullet.expandable, .navigation.app .bullet.collapsible').addClass('border-hover');
        },
        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
            this.FindWrappingLinks('#gb-footer .footer-bottom li', 175);
        },
        "WindowScroll": function(){ 
        	this.StickyHeader();
        },


//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {
            if( this.GetBreakPoint() == "desktop" ){
            	//Desktop

            }else{
            	//BP's
            }
        },
        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },
        "CustomDropdowns": function() {
        	/* customDropdowns
            **  Use: Sets up custom dropdown menus.
            */
        	$('.custom-dropdown input, .custom-dropdown a, .custom-dropdown select').attr('tabindex', -1);

            $('.custom-dropdown > button').off('click keydown').on('click keydown', function(e) { // Trigger the click event from the keyboard
                var code = e.keyCode;
                // 13 = Return, 32 = Space
                if (e.type == "click" || code === 13 || code === 32) {
                	clearTimeout(this.DropdownTimeout);

                    if( $(this).attr("aria-expanded") == "false" ){
                        CreativeTemplate.ClearDropdowns();
                        var dropdown = $('~ .dropdown', this);
                        dropdown.attr('aria-hidden', 'false').slideDown();
                        CreativeTemplate.DropdownKeyboardFunctionality(dropdown);
                        $('a, input, select', dropdown).first().focus();

                        $(this).attr('aria-expanded', 'true');
                        $(this).parent().addClass("openMenu");
                    }else{
                        CreativeTemplate.ClearDropdowns();
                    }
                }else if((code == 39 || code == 40) && $(this).attr('aria-expanded') == 'true'){
                    e.preventDefault();
                    if( $('~ .dropdown select', this).length <= 0 ){
                        $('~ .dropdown a', this).first().focus();
					}else{
                    	$('~ .dropdown select', this).first().focus();
                    }
                }else if( code == 9 ){
                    if($(this).attr('aria-controls') != 'translate-option-list'){
                        CreativeTemplate.ClearDropdowns();
                    }
                }
            });

            $('.custom-dropdown').hover(function(e){ //In
                clearTimeout(CreativeTemplate.DropdownTimeout);
            },function(e){ //Out
                //canDropDownHide = true;
                CreativeTemplate.DropdownTimeout = setTimeout(function(){
                    CreativeTemplate.ClearDropdowns();
                }, 800);
            });
        }, /* Self-Made Dropdown - Main Function and Event Handlers*/
        "ClearDropdowns": function(){
        	$('.custom-dropdown.openMenu input, .custom-dropdown.openMenu a, .custom-dropdown.openMenu .custom-dropdown > button').attr('tabindex', -1);
            $('.custom-dropdown.openMenu .dropdown').attr('aria-hidden', 'true').slideUp();
            $('.custom-dropdown.openMenu > button').removeClass('acc-open').attr('aria-expanded', 'false');
            $('.custom-dropdown.openMenu li').removeClass('active-list-item');
            this.DropdownKeyboardFunctionality($('.custom-dropdown.openMenu .dropdown'));
            $('.custom-dropdown.openMenu').removeClass("openMenu");
        }, /* Self-Made Dropdown - Clears the dropdowns */
        "DropdownKeyboardFunctionality": function(dropdown){
            $('a, input, select', dropdown).attr('tabindex', -1).off('keydown').keydown(function(e){
                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                var linkObj = $('a, input, select', dropdown);

                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                            // IS FIRST ITEM
                        if(linkObj.index($(this)) == 0) {
                        	if($(this).hasClass('section-item')){
                            	$('.sw-channel-item.active > a').focus();
                            }else{
                            	$(dropdown).siblings('button').focus();
							}
                        } else {
                            linkObj[linkObj.index($(this)) - 1].focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                        	if($(this).hasClass('section-item')){
                            	$('.sw-channel-item.active > a').focus();
                            }else{
                                // FOCUS FIRST ITEM
                                linkObj[0].focus();
							}
                        } else {
                            // FOCUS NEXT ITEM
                            linkObj[linkObj.index($(this)) + 1].focus();
                        }
                    break;

                    case csKeyCodes.tab:
                    	CreativeTemplate.ClearDropdowns();
                    break;

                    case csKeyCodes.esc:
                    	e.preventDefault();
                        $(dropdown).siblings('button').focus();
                        CreativeTemplate.ClearDropdowns();
                    break;

                    default: //pass
                    break;
                }
            });
        }, /* Self Made Dropdown - a11y Event Handlers */
        "ModalKeyboardEventFunctionality": function(modalPanel){
        	var _this = this;

            $('a, button, input', modalPanel).keydown(function(e){
                var linkObj = $('a, button, input', modalPanel);
                var lastTabIndex = $(".cs-tab", modalPanel).length - 1;

                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case CreativeTemplate.KeyCodes.left:
                    case CreativeTemplate.KeyCodes.up:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                            // IS FIRST ITEM
						if($(this).hasClass('cs-school') && linkObj.index($('.cs-panel[aria-hidden="false"] a:first', modalPanel)) == linkObj.index($(this))){
                        	$('.cs-tab[aria-selected="true"]', modalPanel).focus();
                        }else if(linkObj.index($(this)) == 0) {
                        	if($(this).hasClass('schools-close')){
                                $('.cs-panel[aria-hidden="false"] a:last', modalPanel).focus();
                            }else{
								linkObj[linkObj.length - 1].focus();
                        	}
                        } else {
                        	if($(this).hasClass('cs-tab')){
                            	if($(this).attr('data-index') == 0){
                                    _this.SwitchTabs($(this), $('.cs-tab[data-index="' + lastTabIndex + '"]', modalPanel), modalPanel);
								}else{
                                    var prevTab = $(".cs-tab[data-index='" + (parseInt($(this).attr("data-index")) - 1) + "']", modalPanel); // PREVIOUS TAB
                                    _this.SwitchTabs($(this), prevTab, modalPanel);
								}
                            }else {
                            	linkObj[linkObj.index($(this)) - 1].focus();
                            }
                        }
                    	break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case CreativeTemplate.KeyCodes.right:
                    case CreativeTemplate.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if(linkObj.index($(this)) == (linkObj.length - 1) || ($(this).hasClass('cs-school') && linkObj.index($('.cs-panel[aria-hidden="false"] a:last', modalPanel)) == linkObj.index($(this)))) {
                            // FOCUS FIRST ITEM
                            $('.modal-close-trigger', modalPanel).focus();
                        } else {
                            // FOCUS NEXT ITEM
                            if($(this).hasClass('cs-tab')){
                            	if($(this).attr('data-index') == lastTabIndex){
                                    _this.SwitchTabs($(this), $('.cs-tab[data-index="0"]', modalPanel), modalPanel);
								}else{
                                	var nextTab = $(".cs-tab[data-index='" + (parseInt($(this).attr("data-index")) + 1) + "']", modalPanel); // PREVIOUS TAB
                                    _this.SwitchTabs($(this), nextTab, modalPanel);
                                }
                            } else {
                            	linkObj[linkObj.index($(this)) + 1].focus();
                            }
                        }
                        break;

                    case CreativeTemplate.KeyCodes.tab:
                    	e.preventDefault();
                        e.stopImmediatePropagation();

                    	if(e.shiftKey){
                        	if($(this).hasClass('cs-school') && linkObj.index($('.cs-panel[aria-hidden="false"] a:first', modalPanel)) == linkObj.index($(this))){
                                $('.cs-tab[aria-selected="true"]', modalPanel).focus();
                            }else if(linkObj.index($(this)) == 0) {
                            	if($(this).hasClass('schools-close')){
                                    $('.cs-panel[aria-hidden="false"] a:last', modalPanel).focus();
                                }else{
                                	linkObj[linkObj.length - 1].focus();
								}
                            } else {
                            	if($(this).hasClass('cs-tab')){
                                	$('.modal-close-trigger', modalPanel).focus();
                                }else{
                                	linkObj[linkObj.index($(this)) - 1].focus();
                            	}
                            }
                        }else{
                        	if(linkObj.index($(this)) == (linkObj.length - 1) || ($(this).hasClass('cs-school') && linkObj.index($('.cs-panel[aria-hidden="false"] a:last', modalPanel)) == linkObj.index($(this)))) {
                                // FOCUS FIRST ITEM
                                $('.modal-close-trigger', modalPanel).focus();
                            } else {
                            	if($(this).hasClass('cs-tab')){
                                	//focus first item in panel
                                	$('.cs-panel[aria-hidden="false"] a:first', modalPanel).focus();
                                }else{
                                	// FOCUS NEXT ITEM
                                    linkObj[linkObj.index($(this)) + 1].focus();
                                }
                            }
                        }
                    	break;

                    case CreativeTemplate.KeyCodes.esc:
                    	e.preventDefault();
                        e.stopImmediatePropagation();
                    	$('.modal-close-trigger', modalPanel).click();
                    	break;

                    default: //pass
                    break;
                }
            });
        },
        "SwitchTabs": function(currTab, nextTab, element) {
            // SWITCH TAB
            $(currTab).removeClass("active").attr("aria-selected", "false").attr("tabindex", "-1");
            $(nextTab).addClass("active").attr("aria-selected", "true").attr("tabindex", "0").focus();

            // SWITCH PANEL
            var nextIndex = parseInt(nextTab.attr("data-index"));
            $("div.ui-widget.app.activeTabApp", element).removeClass("active-app");
            $("div.ui-widget.app.activeTabApp:eq(" + nextIndex + ")", element).addClass("active-app");

            $(".cs-panel", element).attr("aria-hidden", "true");
            $(".cs-panel[data-index='" + nextIndex + "']", element).attr("aria-hidden", "false");
        },

// SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "BuildStylesheet": function() {
        	var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var rules = document.createTextNode(this.DynamicStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = this.DynamicStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },


// GETTERS
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }, 
        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },
        "CutHex": function(hex){
        	return (h.charAt(0)=="#") ? hex.substring(1,7):hex;
        },


