        "ChannelBar": function() {
            //Overwrite system hover functionality for an animated version.
            $('#channel-navigation > .sw-channel-item > a').off('touchstart');
            $('.sw-channel-dropdown a').addClass('section-item');
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){ //in
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.stop(true, true).slideDown(300, "swing");

                    subList.css('left', '');

                    var offsetLeft = (-(subList[0].offsetLeft) - this.offsetLeft + 1);

                    if(offsetLeft > 0 ){
                    	subList.css('left', subList[0].offsetLeft + offsetLeft);
                    }

                    var offsetRight = ($('#gb-channel-list')[0].offsetLeft + this.offsetLeft + this.offsetWidth + -(subList[0].offsetLeft) + 25) - $(window).width();

                    if(offsetRight > 0){
                    	subList.css('left', subList[0].offsetLeft - offsetRight);
                    }
                }

                $(this).addClass("hover");
            }, function(){ //out
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $('.sw-channel-item').keydown(function(e){
            	var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    CreativeTemplate.DropdownKeyboardFunctionality(subList);
            	}
            });

