            $('.sw-channel-item:not(#navc-HP):not(#navc-Cal)').each(function(channelIndex, channel){
                if( $('.sw-channel-dropdown li', channel).length > 0 ){
                	if( channelIndex < 6 ){
                        var sectionsAdded = 0;
                        var colsAdded = 0;
                        var colHtml = "";

                        for(var colCount = 0; colCount < megaMenuData[channelIndex].length; colCount++){
                            if(megaMenuData[channelIndex][colCount].sections > 0){
                                colHtml += "
";
                                if(megaMenuData[channelIndex][colCount].header != ""){
                                    colHtml += "
" + megaMenuData[channelIndex][colCount].header + "";
                                }
                                colHtml += "";

