                                    if(sectionsAdded < $('.sw-channel-dropdown li', channel).length){
                                        colHtml += $('.sw-channel-dropdown li', channel).eq(sectionsAdded)[0].outerHTML;
                                        sectionsAdded++;
                                    }else{
