        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

			$("div.ui-widget.app").each(function(appIndex, app){
            	//more link
                if( $('.more-link', app).length > 0 ){
                    $('.more-link', app).prependTo( $('.ui-widget-footer', app) );
                    $('.more-link-under', app).parent().remove();
                }

                //view calendar link
                if( $('.view-calendar-link', app).length > 0 ){
                    $('.view-calendar-link', app).prependTo( $('.ui-widget-footer', app) );
                }

                if( $('.app-level-social-follow', app).length > 0 && $('.app-level-social-follow', app).children().length > 0 ){
                    $('.app-level-social-follow', app).prependTo( $('.ui-widget-footer', app) );
                }else{
                	$('.app-level-social-follow', app).remove();
                }

                //Hide empty footer
                if( $('.ui-widget-footer', app).children().not('.clear').length <= 0 ){
                	$('.ui-widget-footer', app).addClass('no-children').hide();
                }else{
                	$('.ui-widget-footer', app).addClass('has-children');
                    $('.ui-widget-footer .clear', app).remove();
                }
            });

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            $('.more-link, .view-calendar-link').addClass('border-hover rounded-corners');


            if($('.sp').length > 0){
                // ADJUST FIRST BREADCRUMB
                $("li.ui-breadcrumb-first > a > span").text("Home");


                // SUB PAGE HEADER
                $('.sp-column.one .ui-widget-header > *')
                	.html('Page Navigation')
                	.append('');

                var spHeaderText = 'COVID-19';
                if(spHeaderText == ""){
                	if($('.ui-widget.app').length == 1 && $('.ui-widget.app.calendar').length) spHeaderText = "Calendar";

                    if($('#swlogin').length) spHeaderText = "Sign In";
                    //console.log(window.location.href.indexOf('PageType=10'));
                    if(window.location.href.indexOf('PageType=10') >= 0) spHeaderText = "Register";
                }
                $("#sp-page-header").append("
" + spHeaderText + "");

                var channelName = "Community".trim().replace(/\s/g, '-').toLowerCase();
                var sectionName = "COVID-19".trim().replace(/\s/g, '-').toLowerCase();

                var possibleImageSrcs = [
                    "/cms/lib/TX01918037/Centricity/domain/4/subpage-background/" + channelName + "/" + sectionName + ".jpg", //section image
                    "/cms/lib/TX01918037/Centricity/domain/4/subpage-background/" + channelName + "/default.jpg", //channel default
                    "/cms/lib/TX01918037/Centricity/domain/4/subpage-background/default.jpg" //main default
                ];
                var spHeaderImage = new XMLHttpRequest();

                for(var image = 0; image < possibleImageSrcs.length; image++){
                    spHeaderImage.open('HEAD', possibleImageSrcs[image], false);
                    spHeaderImage.send();

                    if(spHeaderImage.status == 200){
						$('#sp-page-header').css('background', 'url("' + possibleImageSrcs[image] + '") no-repeat center center');
                        break
                    }
                }	
            }else{
            	if( $('#hp-emergency-announcement .ui-articles').children().length > 0 ){
                	$('#hp-emergency-announcement').show(0);

                    $('#close-emergency-announcement').click(function(e){
                    	$('#hp-emergency-announcement').slideUp({
                            "duration": 400,
                            "step": function(e){
                                $(window).trigger('resize');
                            },
                            "done": function(e){
                                $(this).remove();
                            }
                        });
                    });
                }else{
                	$('#hp-emergency-announcement').remove();
                }

                $('.hp-row').each(function(rowIndex, row){
                	if($('.hp-column', row).length > 0){
                    	$('.hp-column', row).each(function(colIndex, col){
                        	if( $('.region', col).children().length <= 0 ) $(col).remove();
                        });
                    }

                    if($('.region', row).children().length <= 0 || $(row).children().length <= 0 ){
                    	//console.log('removing row');
                        //console.log(row);
						$(row).remove();
                    }
                });
            }


            $('#hp-scroll-down').click(function(e){
				CreativeTemplate.SmoothScroll('#hp-content', 55);
            });

            //Customize Apps
            this.UpcomingEvents();
        },
        "Footer": function() {
        	//DOC get Schoolwires footer links
            $("a#feedback").text($("div#sw-footer-links li:eq(0) a").text()).attr("href", $("div#sw-footer-links li:eq(0) a").attr("href"));
            $("a#terms").text($("div#sw-footer-links li:eq(2) a").text()).attr("href", $("div#sw-footer-links li:eq(2) a").attr("href"));
            $("a#policy").text($("div#sw-footer-links li:eq(1) a").text()).attr("href", $("div#sw-footer-links li:eq(1) a").attr("href"));		
