        "Search": function() {
        	var defSearchText = "SEARCH";

            var searchBox = $('#gb-search-input');

            searchBox
                .attr("placeholder", '')
                .val(defSearchText)
                .focus(function() {
                    if($(this).val() == defSearchText) {
                        $(this).val("");
                    }
                })
                .blur(function() {
                    if($(this).val() == "") {
                        $(this).val(defSearchText);
                    }
                });

            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($("#gb-search-input").val() != "" && $("#gb-search-input").val() != defSearchText) {
                    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });
        },
        "SmoothScroll": function(to, offset){
        	/*
            ** smoothScroll
            ** Use: This will smoothly scroll to the id that was passed to the function.
            ** Return: N/A
            ** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
            ** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
            */
        	if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
            var target = $(to);

            if (target.length) {
                $('html,body').animate({
                    scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
                }, 800, function(){
                    $("html, body").stop(true, false);
                });
                return false;
            }
        },
        "CanvasPainter": function(area){
        	var _this = this;
        	var canvasElement;

            switch(area){
            	case "logo":
                	if(document.getElementById('logo-triangle') != undefined){
                    	canvasElement = document.getElementById('logo-triangle').getContext('2d');
                        _this.CanvasShape(canvasElement, 'bottom-isoceles', 184, 27);
                        canvasElement.fillStyle = '#fff';
                        canvasElement.fill();
                    }
                	break;

                default:
                	break;
            }

        },
        "CanvasShape": function(canvas, shape, width, height, extraProps){
        	switch(shape){
                case "bottom-isoceles":
                	canvas.beginPath();
                    canvas.moveTo(0, 0);
                    canvas.lineTo(width, 0);
                    canvas.lineTo(width/2, height);
                    canvas.lineTo(0, 0);
                	break;

                default:
                	break;
            }
        },
        "StickyHeader": function() {
            var getWidth = $(window).width();
            if (getWidth >= 1024) {
                // CHANNEL BAR
                var gbNavOffSet = $("#gb-sticky-header-outer").offset().top + 220;

                if ($(window).scrollTop() <= gbNavOffSet) {
                    if(CreativeTemplate.csNavIsSticky) {  
                        $("#gb-sticky-header-outer").css("height", "");
                        $("#gb-sticky-header-outer, #gb-page").removeClass("sticky-on");
                        CreativeTemplate.csNavIsSticky = 0;
                    }
                } else {

                    if(!CreativeTemplate.csNavIsSticky) {
                        $("#gb-sticky-header-outer").css("height", $("#gb-sticky-header").outerHeight(true));
                        $("#gb-sticky-header-outer, #gb-page").addClass("sticky-on");
                        CreativeTemplate.csNavIsSticky = 1;
                    }

                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        if($(".wcm-controls").hasClass("wcm-stuck")) {
                            $(".wcm-controls").css("margin-top", $("#gb-sticky-header").outerHeight(true));
                        }
                    }
                }

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + (($("#gb-sticky-header-outer").outerHeight(true) > 150) ? $("#gb-sticky-header-outer").outerHeight(true) : 175) + "px"
                });
            } else {
                if(CreativeTemplate.csNavIsSticky == 1) {
                    $("#gb-sticky-header-outer, #gb-page").removeClass("sticky-on");
                    CreativeTemplate.csNavIsSticky = 0;
                }
                $("#gb-sticky-header-outer").css("height", '');

                $("#sw-maincontent").removeAttr("style");
            }
        },
        "AnimateText": function(icon, action){
        	switch(action){
            	case "show":  
                	$('.text', icon).stop(true, true).animate({
                    	'width': $(icon).attr('data-text-width'),
                        'padding-right': 82,
                        'padding-left': 2
                    }, 400);
                	break;

                case "hide":
                	$('.text', icon).stop(true, true).animate({
                    	'width': 0,
                        'padding-right': 0,
                        'padding-left': 0
                    }, 400);
                	break;

				default:
                	break;
            };
        },
        "SchoolTabList": function(){
            if( $('.sw-mystart-dropdown.schoollist').length > 0 ){
            	var hsList = '';
                var msList = '';
                var esList = '';
                var spList = '';
                var hsListItemsAdded = 0;
                var msListItemsAdded = 0;
                var esListItemsAdded = 0;
                var spListItemsAdded = 0;

                //... because that's how it is I guess
                $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a[href='/Domain/894']").parent().remove();
                $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a[href='/Domain/76']").parent().remove();
                $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a[href='/Domain/896']").parent().remove();
                $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a[href='/Domain/897']").parent().remove();
                $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a[href='/Domain/898']").parent().remove();
                $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a[href='/Domain/899']").parent().remove();
                $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a[href='/Domain/8344']").parent().remove();
                $(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a[href='/Domain/7809']").parent().remove(); //71
  				//$(".sw-mystart-dropdown.schoollist .sw-dropdown-list li a[href='/Domain/47']").parent().remove(); //73


                //break the schools out by their type and create a new UL every 14 items.
                $('.sw-mystart-dropdown.schoollist .sw-dropdown-list li').each(function(k, v){ 
                    if($('a', v).text().search( /\sHS|\sHigh|\shigh|\shs/g) >= 0){
                        if(hsListItemsAdded != 0 && hsListItemsAdded%7 == 0) hsList += '';
                        hsList += '
' + $(v).html() + '';
                        hsListItemsAdded++;
                    }else  if($('a', v).text().search( /\sMS|\sMiddle|\smiddle|\sms/g) >= 0){
                        if(msListItemsAdded != 0 && msListItemsAdded%10 == 0) msList += '';
                        msList += '
' + $(v).html() + '';
                        msListItemsAdded++;
                    }else if($('a', v).text().search( /\sES|\sElem|\selem|\ses/g) >= 0){
                        if(esListItemsAdded != 0 && esListItemsAdded%14 == 0) esList += '';
                        esList += '
' + $(v).html() + '';
                        esListItemsAdded++;
                    }else{
                    	if(spListItemsAdded != 0 && spListItemsAdded%12 == 0) spList += '';
                        spList += '
' + $(v).html() + '';
                        spListItemsAdded++;
                    }
                });


                hsList += "";
                msList += "";
                esList += "";
                spList += "";

                $('#cs-elementary-schools-panel').html(esList);
                $('#cs-middle-schools-panel').html(msList);
                $('#cs-high-schools-panel').html(hsList);
                $('#cs-speciality-schools-panel').html(spList);
        	}

            $('#gb-schools-modal .cs-panels a').addClass('cs-school');

            var schoolDropdownParent = $('#cs-school-dropdown-wrapper');

            // INIT FIRST PANELS
            $('.cs-tab:first', schoolDropdownParent).attr('aria-selected', 'true');
            $('.cs-panel:first', schoolDropdownParent).attr('aria-hidden', 'false');
            $('.cs-panel a', schoolDropdownParent).attr('tabindex', -1);

            // TAB CLICK
            $(".cs-tab", schoolDropdownParent).click(function() {
                CreativeTemplate.SwitchTabs($(".cs-tab[aria-selected='true']", schoolDropdownParent), $(this));
            });

            // TAB KEYDOWN
            $("button.cs-tab", schoolDropdownParent).off('keydown').keydown(function(e) { 
            	e.preventDefault();
                e.stopPropagation();
                CreativeTemplate.ModalKeyboardEventFunctionality($('.modal-panel[aria-hidden="false"]'));
            });

            // PANEL KEYDOWN
            $(".cs-panel a", schoolDropdownParent).off('keydown').keydown(function(e) { 
				CreativeTemplate.ModalKeyboardEventFunctionality($('.modal-panel[aria-hidden="false"]'));
            });

            // HOOK MAIN HANDLE EVENT
            $('#cs-school-dropdown-trigger').click(function(e){
            	if($(this).attr('aria-expanded') == 'true'){
                	$('#cs-school-dropdown-wrapper')
                    	.attr('aria-hidden', 'true')
                        .slideUp(400);
                    $(this).attr('aria-expanded', 'false');
                }else{
                	$('#cs-school-dropdown-wrapper')
                    	.attr('aria-hidden', 'false')
                        .stop(true)
                        .slideDown(400);
                    $(this).attr('aria-expanded', 'true');

                    $('#cs-school-dropdown-wrapper .cs-tab[aria-selected="true"]').focus();
				}
            });
        },
        "FindWrappingLinks": function(links, assumedOffset){
        	$(links).each(function(linkIndex, link){
            	if($(link).position().top > (assumedOffset + 25)){
                	$(link).addClass('wrapped');
                }else if($(link).hasClass('wrapped')){
                	$(link).removeClass('wrapped');
                }
            });
        },

        "PasswordResetPage": function(){ /* Added by Jeremy K - Case #04550898 */
            const pageTypeIndex = window.location.href.indexOf('PageType=') + 9;
            if(pageTypeIndex > 9 && window.location.href.slice(pageTypeIndex, pageTypeIndex + 2).replace('&', '') == 9){
				console.log('on password reset page');
                if($('#sw-forgotpassword').length > 0){
                	$('#sw-forgotpassword .ui-widget-header, #sw-forgotpassword .ui-widget-footer').html('');
                    $('#sw-forgotpassword .ui-widget-detail').html('Click Here for the Password Reset Service');

                    $('#sw-forgotpassword').addClass('loaded');
                }
			}
        },


