link.script:
$(function() {
	var iconsPerRow38822 = [];
    var iconRowIndex38822 = 0;
    var iconCounter38822 = 0;
    var iconPosition38822 = -1;

	$("#pmi-38822 .icon").each(function(index) {
        if($(this).hasClass("library")) {
            var iconSelection = $(this).attr("data-icon").toLowerCase().split("/");
            var iconCategory = iconSelection[0].replace(/\s+/g, "-");
            var icon = (iconSelection[1].split("."))[0];
            var finalIcon = "sw-icon-" + iconCategory + "-" + icon;

            $(this).addClass(finalIcon);
        }
    });

    setIconHeight38822();
    $(window).resize(function() { setIconHeight38822(); });

    function setIconHeight38822() {
    	$("#pmi-38822 .icon").css("height", "");

        var iconRows38822 = [];
        var iconRowIndex38822 = 0;
        var iconStartRange38822 = 1;
        var iconEndRange38822 = 1;
        var iconPosition38822 = -1;
        var iconHeight38822 = 0;

        $("#pmi-38822 .ui-articles > li").each(function(index) {
            var topOffset = $(this).offset().top;

            if(index == $("#pmi-38822 .ui-articles > li").length - 1) {
            	if(topOffset != iconPosition38822) {
                	if($(".icon", this).height() > iconHeight38822) {
                        iconHeight38822 = $(".icon", this).height();
                    }

                    iconRows38822[iconRowIndex38822] = {
                        "height": iconHeight38822,
                        "range": ":nth-child(n+" + iconStartRange38822 + "):nth-child(-n+" + iconEndRange38822 + ")"
                    }

                    iconStartRange38822 = iconEndRange38822 + 1;
                    iconRowIndex38822++;
                }

                iconEndRange38822++;

                if($(".icon", this).height() > iconHeight38822) {
                    iconHeight38822 = $(".icon", this).height();
                }


                iconRows38822[iconRowIndex38822] = {
                    "height": iconHeight38822,
                    "range": ":nth-child(n+" + iconStartRange38822 + "):nth-child(-n+" + iconEndRange38822 + ")"
                }
            }

            else if(iconPosition38822 == -1) {
                iconPosition38822 = topOffset;

                iconHeight38822 = $(".icon", this).height();
            }

            else if(topOffset == iconPosition38822) {
                iconEndRange38822++;

                if($(".icon", this).height() > iconHeight38822) {
                	iconHeight38822 = $(".icon", this).height();
                }
            }

            else {
            	if($(".icon", this).height() > iconHeight38822) {
                	iconHeight38822 = $(".icon", this).height();
                }

                iconRows38822[iconRowIndex38822] = {
                	"height": iconHeight38822,
                    "range": ":nth-child(n+" + iconStartRange38822 + "):nth-child(-n+" + iconEndRange38822 + ")"
                }

                iconHeight38822 = $(".icon", this).height();
                iconPosition38822 = topOffset;
                iconStartRange38822 = iconEndRange38822 + 1;
                iconEndRange38822++;
                iconRowIndex38822++;
            }
        });

        $.each(iconRows38822, function(index, iconRow) {
        	$("#pmi-38822 .ui-articles > li" + iconRow.range + " .icon").css("height", iconRow.height);
        });
    }

});

