	i[ 'GoogleAnalyticsObject' ] = r;
	i[ r ] = i[ r ] || function() {
		( i[ r ].q = i[ r ].q || [] ).push( arguments )
	}, i[ r ].l = 1 * new Date();
	a = s.createElement( o ),
		m = s.getElementsByTagName( o )[ 0 ];
	a.async = 1;
	a.src = g;
	m.parentNode.insertBefore( a, m )
} )( window, document, 'script', '//www.google-analytics.com/analytics.js', 'gaplusu' );

function beehive_ga_track() {
	gaplusu( 'create', 'UA-13149062-1', 'auto' ); // Create network tracking.
	gaplusu( 'send', 'pageview' ); // Send pageview.
	gaplusu( 'create', 'UA-103101218-1', 'auto', { 'name': 'single' } ); // Create single site tracking.
	gaplusu( 'single.send', 'pageview' ); // Send pageview.
}

