var addthis_config = addthis_config || {};
addthis_config.services_exclude = 'email';

// The tabindex defaults to 1 - which FORCES the tab order to start with the AddThis buttons usually at the bottom of an article page.
// Why do they even add a tabindex attributes to  tags which receive keyboard focus naturally?
// Setting the tabindex for these  tags to zero will cause them to receive focus in the "tabbing flow" of the document (https://davidwalsh.name/tabindex-focus).
//
// https://www.addthis.com/academy/the-addthis_config-variable/
//   ui_tabindex = Sets the global value of the tabindex for all AddThis buttons on the page.
addthis_config.ui_tabindex = 0;

(function($){
  $(function() {
    $('#byline a').click(function() {
      var x = window.open(this.href, 'byLine', 'width=650,height=510,scrollbars=1,resizable=1');
      x.focus();
      return false;
    });

    // Wait until all of the images in the grid are done loading
    $('.ig-thumbnails').imagesLoaded(function() {
      $('.ig-thumbnails').each(function() {
        var $this = $(this), height = 0;

        // Get the height of the tallest image in the gallery
        $this.find('img').each(function() {
          if ($(this).height() > height) height = $(this).height();
        });

        // Use that to set the height and max-height of the image contaner and image, respectively.
        $this.find('.thumbnail-inner').css('height', height).end().find('img').css('max-height', height);

        // Get the combined height of credits and captions
        height = 0;
        $this.find('.ig-show-captions-content').each(function() {
          var $this = $(this), x = 0;
          x = $this.find('.ig-credit').outerHeight() + $this.find('.ig-caption').outerHeight();
          if (x > height) height = x;
        });

        // Cap the height at 100px
        if (height > 100) height = 100;

        if (height == 0) {
          // If there are no captions/credits - hide the checkbox
          $this.find('> .checkbox').addClass('hidden');
        } else {
          // Set the height when the checkbox is clicked (checkbox acts as a toggle on the height).
          $this.find('.ig-show-captions').on('click', function() {
            var $this = $(this);
            if ($this.is(':checked')) {
              $this.closest('.ig-thumbnails').find('.ig-show-captions-content').css('height', height + 4);
            } else {
              $this.closest('.ig-thumbnails').find('.ig-show-captions-content').css('height', 0);
            }
          });
        }

      });
    });

  });
})(jQuery);

