    function ShowGallery(item) {
        new BstrapModal(item.dataset.title).Show();

    }


    var BstrapModal = function (title, body, buttons) {
        var title = title || "";
        var body = body || "";
        var GetModalStructure = function () {
            var that = this;
            that.Id = BstrapModal.Id = "myModalLabel";  //Math.random();
            return "
" +
                    "
" +
                        "
" +
                            "
" +
                                    "×  " +
                                    "" + //body +
                            "" +
                            "
" +
                                "
" +
                                    "<i class='fa fa-chevron-left fa-2x'' id='show-previous-image'>" +
                                "" +
                                "
" +
                                    "." +
                                "" +
                                "
" +
                                    "" +
                                "" +
                            "" +
                        "" +

                    "" +
                ""
        }();
        BstrapModal.Delete = function () {
            var modals = document.getElementsByName("dynamiccustommodal");
            if (modals.length > 0) document.body.removeChild(modals[0]);
        };
        BstrapModal.Close = function () {
            $(document.getElementById(BstrapModal.Id)).modal('hide');
            BstrapModal.Delete();
        };
        this.Show = function () {
            //var btns = document.querySelectorAll("button[name='btn" + BstrapModal.Id + "']");
            //for (var i = 0; i < btns.length; i++) {
            //    btns[i].addEventListener("click", buttons[i].Callback || BstrapModal.Close);
            //}
            $(document.getElementById(BstrapModal.Id)).modal('show');
        };
        this.Load = function () {
            BstrapModal.Delete();
            document.body.appendChild($(GetModalStructure)[0]);
        };
    };




    $(document).ready(function () {
        new BstrapModal().Load();


        loadGallery(true, 'a.gallery-thumbnail');

        //This function disables buttons when needed
        function disableButtons(counter_max, counter_current) {
            //debugger;
            $('#show-previous-image, #show-next-image').show();
            if (counter_max == counter_current) {
                $('#show-next-image').hide();
            } else if (counter_current == 1) {
                $('#show-previous-image').hide();
            }
        }

        function loadGallery(setIDs, setClickAttr) {
            var current_image,
                selector,
                counter = 0;

            $('#show-next-image, #show-previous-image').click(function () {
                if ($(this).attr('id') == 'show-previous-image') {
                    current_image--;
                } else {
                    current_image++;
                }

                selector = $('[data-image-id="' + current_image + '"]');
                updateGallery(selector);
            });

            function updateGallery(selector) {
                var $sel = selector;
                current_image = $sel.data('image-id');
                $('#image-gallery-caption').text($sel.data('caption'));
                $('#image-gallery-title').text($sel.data('title'));
                $('#image-gallery-image').attr('src', $sel.data('image'));

                disableButtons(counter, $sel.data('image-id'));
            }

            if (setIDs == true) {
                $('[data-image-id]').each(function () {
                    counter++;
                    $(this).attr('data-image-id', counter);
                });
            }
            $(setClickAttr).on('click', function () {
                updateGallery($(this));
            });
        }
    });

googletranslate.script:
function googleTranslateElementInit()
{
    new google.translate.TranslateElement({
        pageLanguage: 'en',
        autoDisplay: false,
        layout: google.translate.TranslateElement.InlineLayout.SIMPLE
    }, 'google_translate_element');
}

body.script:
    $(document).ready(function () {

    });


    function setCookie(cname, cvalue, exdays) {
        var d = new Date();
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
        var expires = "expires=" + d.toUTCString();
        document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
    }

    function getCookie(cname) {
        //debugger;
        var name = cname + "=";
        var decodedCookie = decodeURIComponent(document.cookie);
        var ca = decodedCookie.split(';');
        for (var i = 0; i < ca.length; i++) {
            var c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }
