function csGoToSearchResults() {
    if($.trim($('#sw-search-input').val()) != '' && $.trim($('#sw-search-input').val()).toLowerCase() != 'search') {
        window.location.href="/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $('#sw-search-input').val();
    } else {
        return false;
    }
}

