function googleTranslateElementInit() {
  new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.FloatPosition.TOP_LEFT, gaTrack: true, gaId: 'UA-61185818-1'}, 'google_translate_element');
  document.getElementsByClassName('goog-te-combo')[0].setAttribute("title", "Google Translate language");
}

