	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 

			break;
			case "768":

			break;
			case "640":

			break;
			case "480":
				getSocial();
			break;
			case "320":
				getSocial();
			break;
		}
	}

    function customSchoolSearch(){
    	if($("#search-options").length){
        	alphaSetup();
            alphaClick();
            typeClick();
            allClick();
        }
    }

    function alphaSetup(){
    	//CREATE AN EMPTY ARRAY FOR SCHOOL FIRST LETTERS
        var schoolLetters = [];
        var schoolType = "";
        var magnetSchool = "";

        $(".school-info").each(function(){
            var firstLetter = $(this).find("h3.percent").text().charAt(0);
            var schoolTypeRaw = $(this).find(".fraction").text().trim().charAt(0);

            if($(this).find(".magnet").text().trim() == "magnet"){
            	magnetSchool = "true";
            } else {
            	magnetSchool = "false";
            }
            switch(schoolTypeRaw){
            	case "9":
                    if(magnetSchool === "true"){
                    	schoolType = "High Magnet";
                    } else {
                    	schoolType = "High";
                    }
                break;
                case "6":
                    if(magnetSchool === "true"){
                    	schoolType = "Middle Magnet";
                    } else {
                    	schoolType = "Middle";
                    }
                break;
                case "K": case "P":
                    if(magnetSchool === "true"){
                    	if(schoolTypeRaw == "P"){
                            schoolType = "Elementary Pre-K Magnet";
                        } else {
                            schoolType = "Elementary Magnet";
                        }
                    } else {
                    	if(schoolTypeRaw == "P"){
                            schoolType = "Elementary Pre-K";
                        } else {
                            schoolType = "Elementary";
                        }
                    } 
                break;
                default:
                	schoolType = "none";
                break;
            }

            //IF THE LETTER DOES NOT ALREADY EXIST IN THE ARRAY, PUSH IT INTO THE ARRAY
            schoolLetters.indexOf(firstLetter) === -1 ? schoolLetters.push(firstLetter) : "";  
            //console.log(firstLetter);

            $(this).addClass("first-letter-"+firstLetter+" "+schoolType);
        })

        schoolLetters = schoolLetters.sort();

        var letterHTML = '';

         for(var i in schoolLetters) {
         	letterHTML += ""+schoolLetters[i]+"";
         }

         $("#alphabet-container").prepend(letterHTML);

        //console.log(schoolLetters);
    }

    function alphaClick(){
    	$("button.letter").on('click keypress', function(event){
	        if(globalClickForButtons(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();
	            var firstLetter = $(this).text();
                $("button.school-type, #alphabet-container .letter, #show-all, #by-address").removeClass("active");
                $("#sw-content-minibaselist578 .school-info").hide();
                $("#home-address").slideUp();

                $(this).addClass("active");
                $("#sw-content-minibaselist578 .school-info.first-letter-"+firstLetter).show();
	        }
	    });
    }

    function typeClick(){
    	$("button.school-type").on('click keypress', function(event){
	        if(globalClickForButtons(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();
	            var schoolType = $(this).text();
                $("button.school-type, #alphabet-container .letter, #show-all, #by-address").removeClass("active");
                $("#sw-content-minibaselist578 .school-info").hide();
                $("#home-address").slideUp();

                $(this).addClass("active");
                $("#sw-content-minibaselist578 .school-info."+schoolType).show();
	        }
	    });
    }

    function allClick(){
    	$("#show-all").on('click keypress', function(event){
	        if(globalClickForButtons(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();

                $("button.school-type, #alphabet-container .letter, #by-address").removeClass("active");
                $("#home-address").slideUp();

                $(this).addClass("active");
                 $("#sw-content-minibaselist578 .school-info").show();
	        }
	    });
    }

