/* ------------------------------   NAV LINE  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
function navLine(){
		if ($(".sw-channel-item.active").length) { //added if statement to wrap code below by Lyndsay - Case 00561611 - 6.14.17
            var channelBarHeight = $("nav").height();
            if(channelBarHeight > 64){
                $("#nav-line").css("bottom","64px");
            }
            var $element, leftPos, newWidth,
            $mainNav = $("#channel-navigation");

            if(!$("#nav-line").length){
                $mainNav.append("
");
            }

            var $navLine = $("#nav-line");

            var elementWidth = $(".sw-channel-item.active").width();
            var middleOfElement = elementWidth / 2;

            $navLine
                //.width($(".sw-channel-item.active").width())
                .css("left", $(".sw-channel-item.active").position().left + middleOfElement)
                .data("origLeft", $navLine.position().left)
                .data("origWidth", $navLine.width());

            $("#channel-navigation > li").hover(function() {
                var elementWidth = $(this).width();
                var middleOfElement = elementWidth / 2;
                $element = $(this);
                leftPos = $element.position().left + middleOfElement;
                newWidth = $element.width();
                $navLine.stop().animate({
                    left: leftPos
                    //width: newWidth
                });
            }, function() {
                $navLine.stop().animate({
                    left: $navLine.data("origLeft")
                    //width: $navLine.data("origWidth")
                });    
            });
        }
	}
