/* ------------------------------   GET SOCIAL  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
	function getSocial(){
		if(!$("#get-social-header").hasClass("initiate")){

			$("#get-social-header").click(function(){
				switch(csGetBreakPoint()) {
					case "480":
					case "320":
						if($(this).parent().hasClass("open")){
							$(this).next().slideUp();
							$(this).parent().removeClass("open").addClass("closed");
						} else {
							$(this).next().slideDown();
							$(this).parent().addClass("open");
						}

					break;
				}

			})
			$("#get-social-header").addClass("initiate");
		}
	}

