	function backToTop(){
		$(document).scroll(function() {
			var distanceFromTop = $(this).scrollTop();

				if(distanceFromTop >= 500){
					$("#gb-page").addClass("to-top");
				} else {
					$("#gb-page").removeClass("to-top");
				}
		});
	}

