/* ------------------------------   GLOBAL A11Y CLICK  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
	function globalClick(event){
        if(event.type === 'click'){
            return true;
        }
        else if(event.type === 'keypress'){
            var code = event.charCode || event.keyCode;
            if((code === 32)|| (code === 13)){
                return true;
            }
        }
        else{
            return false;
        }
    }

    //NEED A SPECIFIC GLOBAL CLICK FOR BUTTONS THAT ELIMINATES THE SPACEBAR
    //SINCE IT FUNCTIONS NATIVELY, THE EVENTS HAPPEN TWICE IF WE DO NOT ELIMINATE
    function globalClickForButtons(event){
        if(event.type === 'click'){
            return true;
        }
        else if(event.type === 'keypress'){
            var code = event.charCode || event.keyCode;
            if(code === 13){
                return true;
            }
        }
        else{
            return false;
        }
    }
