	function rsMenu() {
           //ADD HEADER LINKS TO MOBILE MENU FOR 480 AND BELOW
           // function changed by Adam - 9.22.17 - Case #02919530
        	var mobileSocialLinks = [
                ["Facebook", "https://www.facebook.com/NorthEastISD/", "block"],
                ["Twitter", "https://twitter.com/NEISD", "block"],
                ["Instagram", "https://www.instagram.com/northeastisd/", "block"],
                ["YouTube", "https://www.youtube.com/channel/UCj-Ete3dXZ66QxudH2hlkYg/featured", "block"],
                ["LinkedIn", "#", "none"]      
            ];

            var mobileSocial = "";

            $.each(mobileSocialLinks, function(k, v){
                if( v[2] != "none" ) mobileSocial += "1, " + v[0] + ", " + v[1] + ", _blank;";
            }); 

            //ADD GLOBAL HEADER LINKS TO MOBILE MENU - ADDED BY LYNDSAY - CASE 00562274 - 6.19.17
            var popLinks = [
                ["Skyward Family Access", "https://portal.neisd.net/NEISDPortal/public#/SkywardLink/1/1045", "_self"], // ARRAY ORDER - TEXT, LINK, TARGET
                ["Calendars", "/Page/9237", "_self"], // ARRAY ORDER - TEXT, LINK, TARGET
                ["Launchpad", "https://launchpad.classlink.com/neisd", "_blank"], // ARRAY ORDER - TEXT, LINK, TARGET
                ["Staff Directory", "/Page/340", "_self"] // ARRAY ORDER - TEXT, LINK, TARGET
            ];

            var globalLinks = "";

            $.each(popLinks, function(k, v){
                if( v[2] != "" ) globalLinks += "2, " + v[0] + ", " + v[1] + ", " + v[2] + ";";
            });

            var globalLinksHeader = "";

            if( globalLinks != "" ){
                globalLinksHeader = "Popular Links";
            }

            // RESPONSIVE MAIN MENU
            $("body").csRsMenu({
                "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
                "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
                "primaryColor"          : '#1A1A1A', // DEFAULT - #1A1A1A
                "secondaryColor"        : '#0C0C0C', // DEFAULT - #0C0C0C
                "accentColor"           : '#BABABA', // DEFAULT - #BABABA
                "menuButtonParent"      : "#mobile-masthead",
                "showMenuBtnText"       : "yes",
                "menuBtnText"           : "MENU",
                "showSchools"           : "yes",
                "showTranslate"         : "yes",
                "translateId"           : "none",
                "showAccount"           : "yes",
                "extraMenuOptions"      : "Social Media;" + globalLinksHeader,
                "extraMenuItems"        : mobileSocial + globalLinks,  // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET; 
                "siteID"                : "4",
                "allLoaded"             : function(){
                	$('.rs-menu-option.account li a:contains("Sign")').attr("href", "/site/SAMLLogin.aspx?IgnoreRedirect=true"); //added by Lyndsay - Case 02778639 - 7.17.17
                }
            });
    }
