div.script:
( function ( body ) {
	'use strict';
	body.className = body.className.replace( /\btribe-no-js\b/, 'tribe-js' );
} )( document.body );

body.script:
			function googleTranslateElementInit() {
			  new google.translate.TranslateElement({pageLanguage: 'en', includedLanguages: 'en,es', layout: google.translate.TranslateElement.InlineLayout.SIMPLE}, 'google_translate_element');
                setTimeout(function(){
                    jQuery('#goog-gt-tt h1').remove();
                }, 2000)

                setTimeout(function(){
                    jQuery(".goog-te-gadget img").each(function(e){ 
                        jQuery(this).attr("alt","google cleardot") 
                    })
                }, 1000)
            }

            //Override Ninja form multiple parts scroll on page change scroll to top.
            var ninja_forms_scroll_to_top = function( form_id ) {
                var top = jQuery("#ninja_forms_form_" + form_id + "_wrap").offset().top;
                jQuery(window).stop().animate({
                    scrollTop: top
                }, 1000);
                jQuery("#ninja_forms_form_" + form_id + "_wrap").scrollTop(top);
            }    

            // For Sticky Menu Customizing > Advanced CSS, Footer & Sub-site style.css
            jQuery(document).ready(function() {
                var showStaticMenuBar = false;
                jQuery(window).scroll(function() {
                    if(showStaticMenuBar == false){
                        if( jQuery(window).scrollTop() >= 54 ){
                            jQuery("#header-bottom").addClass('positionFixed');
                            jQuery("#sidebarNav").addClass('mobile_positionFixed');
                            showStaticMenuBar = true;
                        }
                    } else {
                        if( jQuery(window).scrollTop() <= 54 ){
                            jQuery("#header-bottom").removeClass('positionFixed');
                            jQuery("#sidebarNav").removeClass('mobile_positionFixed');
                            showStaticMenuBar = false;
                        }
                    }
                });
            });

jQuery(window).load(function() {
    jQuery('#sliderWrap').css('opacity', '1');
});
jQuery(document).ready(function() {
    jQuery('li.school > a').click(function() {
        jQuery(this).next('.urlList').toggle();
        if( jQuery(this).next('.urlList').css('display') == 'block' ) {
            jQuery(this).prev('i').addClass('glyphicon-triangle-bottom');
        } else {
            jQuery(this).prev('i').removeClass('glyphicon-triangle-bottom');
        }
    });

    setTimeout(function(){
        jQuery('#goog-gt-tt h1').remove();
    }, 2000)

    setTimeout(function(){
        jQuery(".goog-te-gadget img").each(function(e){ 
            jQuery(this).attr("alt","google cleardot") 
        })
    }, 1000)
});


