jQuery(window).scroll(function() {
    var sticky = jQuery('.container-section-class-sidebar-application-form'),
        scroll = jQuery(window).scrollTop(),
        screen = jQuery(window).width();
    if( screen > 991 ) {
        if( scroll >= 430 ) {
            sticky.addClass('sidebar-fixed');
        } else {
            sticky.removeClass('sidebar-fixed');
        }
    }
});

jQuery('.module-section .section-content-h2').on('click', function() {
    var $this = jQuery(this);
    if($this.closest('.module-section').find('.expandable').is(':visible'))
        $this.closest('.module-section').find('.expandable').hide("slide");
    else
        $this.closest('.module-section').find('.expandable').show("slide");
});
