	a.async = 1;
	a.src = g;
	m.parentNode.insertBefore( a, m )
} )( window, document, 'script', '//www.google-analytics.com/analytics.js', 'gaplusu' );

function beehive_ga_track() {
	gaplusu( 'create', 'UA-1860442-7', 'auto' ); // Create network tracking.
	gaplusu( 'send', 'pageview' ); // Send pageview.
	gaplusu( 'create', 'UA-1860442-1', 'auto', { 'name': 'single' } ); // Create single site tracking.
	gaplusu( 'single.send', 'pageview' ); // Send pageview.
}

beehive_ga_track(); // Load tracking.

div.script:
