	});	

	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				appViewReset();
			break;
			case "768":
				appViewReset();
			break;
			case "640":
				appViewReset();
			break;
			case "480":
			break;
			case "320":
			break;
		}
	}

