        //added by Matt B - custom hourly - 12.18.18
         $(".ui-widget.app.multimedia-gallery").not($("#hp-slideshow .ui-widget.app.multimedia-gallery")).each(function() {
              var mmg = eval("multimediaGallery" + $(this).attr("data-pmi"));
              mmg.props.defaultGallery = false;

              $(this).csMultimediaGallery({
              	  "efficientLoad" : false,
              	  "linkedElement" 		: ["image"],
                  "playPauseControl" : true,
                  "bullets" : true,
                  "transitionSpeed" : 1.5,
                  "transitionDelay" : 6,
                  "onImageLoad" : function(props) {
                  		$("#spn-content .mmg-container .mmg-slide").each(function(appIndex, app){
                          if($(".mmg-image > a",app).length){
                              var link = $(".mmg-image > a",app).attr("href"); 
                              if($(".mmg-description",app).length){
                              	if(!$(".mmg-description > a",app).length){
                                	$(".mmg-description",app).wrapInner('');
                                }

                              }
                          }
                        });}, // props.element, props.recordIndex, props.mmgRecords
                  "onTransitionStart" : function(props) { // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                      $(".mmg-viewer > .watch-video",props.element).remove();
                  },
                  "onTransitionEnd" : function(props) { // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                      if($(".mmg-container .mmg-slide.active .mmg-description-links .watch-video", props.element).length){
                          $(".mmg-container .mmg-slide.active .mmg-description-links .watch-video", props.element).clone().insertBefore($(".mmg-live-feedback", props.element));
                      }
                  },
                  "allLoaded" : function(props) { // props.element, props.mmgRecords
                      if($(".mmg-container .mmg-slide.active .mmg-description-links .watch-video", props.element).length){
                          $(".mmg-container .mmg-slide.active .mmg-description-links .watch-video", props.element).clone().insertBefore($(".mmg-live-feedback", props.element));

                      }

                      if($(".mmg-container[data-record-num='1']").length){
                      	$(".mmg-container", props.element).addClass("one-record");
                      }

                      if($("#sp-content .mmg-container .mmg-slide .mmg-description-links .read-more").length){
                      		$("#sp-content .mmg-container .mmg-slide .mmg-description-links .read-more").remove();
                      }

                      if($("#spn-content .mmg-container .mmg-slide .mmg-description-links .read-more").length){
                      		$("#spn-content .mmg-container .mmg-slide .mmg-description-links .read-more").remove();
                      }
                      if($("#spn-content .mmg-container .mmg-slide .mmg-description-links .watch-video").length){
                      		$("#spn-content .mmg-container .mmg-slide .mmg-description-links .watch-video").remove();
                      }
                      if($("#sp-content .mmg-container .mmg-slide .mmg-description-links .watch-video").length){
                      		$("#sp-content .mmg-container .mmg-slide .mmg-description-links .watch-video").remove();
                      }
                  },
                  "onWindowResize": function(props) {} // props.element, props.mmgRecords
              });
           });

	}

	function appView() {
		if(!appViewSet) {
			// MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
			if(!$(".hp-content div.ui-widget.app.active").length){
				$(".hp-content div.ui-widget.app:first").addClass("active");
			}

			// DO NOT ADD APP INTO ACCORDION IF IT DOES NOT HAVE A HEADER
			$(".hp-content div.ui-widget.app").each(function() {
				if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
					$(this).addClass("no-header");
				}
			});

			// ACCORDION HEADER CLICK
			$(".hp-content div.ui-widget-header").click(function(){
				var clickedApp = $(this).parent().parent().attr("id");

				$(".hp-content div.ui-widget.app").removeClass("active");
				$(this).parent().addClass("active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			});

			appViewSet = 1;
		}
	}

	function appViewReset() {
		if(appViewSet) {
			$(".hp-content div.ui-widget-header").unbind("click");

			appViewSet = 0;
		}
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
Our District");
		}
        }

