	window.addEvent('domready', function () {
		"use strict";

		var formDonate = $('formDonate'), i;

		document.getElementById('auto_return').value = window.location.href + "?view=confirm&pdt=1&rid=";

		formDonate.getElements('input[class^=contact_]').each(function (element, index) {
			element.addEvent('change', function (event, el) {
				var el = this;
				el.getProperty('class').split(' ').each(function (string) {
					if (string.indexOf('contact_') === 0) {
						$('paypal_standard_' + string.slice(8, string.length)).value = el.value;
					}
				});
			});
		});
