	function trackPayPalStandardDonation()
	{
		"use strict";

		var request,
			response = {'error': false},
			intervalUnit_paypalActions = {'once' : '_donations', 'monthly' : '_xclick-subscriptions'},
			url = window.location.href.replace(window.location.search, '');

		if (!SBX_DONATIONS.validation()) {
			SBX_DONATIONS.validationErrorHandler();
			return false;
		}

		$('paypalAction').value = intervalUnit_paypalActions[$('intervalUnit').value];
		$('formDonate').format.value    = 'raw';
		$('formDonate').processor.value = 'paypal_standard';

		request = new Ajax(
			url,
			{
				method: 'post',
				data: $('formDonate'),
				onRequest: function () {
					$('btn-donatepaypal').setProperty('disabled', 'disabled');
				},
				onComplete: function () {
					$('btn-donatepaypal').removeProperty('disabled');
					response = Json.evaluate(this.response.text);
					if (response.error) {
						displayWarning(response.msg, 'donate-paypalformactions', false);
					} else {
						$('notify_url').value += response.id;
						$('auto_return').value += response.id;
						$('paypalStandardForm').submit();
					}
				}
			}
		).request();

		return false;
	}
