	(function($) {
        $(document).ready(function() {
            $(window.location.hash).removeClass("hide");
            $("a[href*='"+window.location.hash+"']").addClass("active");

            if (!window.location.hash) {
                $("a[href*='#teams']").addClass("active");
                $("#teams").removeClass("hide");
            }

            $(".section-tab .bar a").on("click", function(event) {
                event.preventDefault();
                history.replaceState(null, '', $(this).attr('href'));
                $(this).parent().find("a").removeClass("active");
                $(this).addClass("active");
                $(".section-tab .tab").addClass("hide");
                $(window.location.hash).removeClass("hide");
            });
        });
    })(jQuery.noConflict());

(function($){
	$(document).ready(function() {
		if ($('#card-element').length) {
			var stripe = Stripe('pk_live_hWmulXfRkt0FfZXFEWFDNVr2');
			var elements = stripe.elements();

			// Custom styling can be passed to options when creating an Element.
			var style = {
				base: {fontSize: '16px', color: '#32325d'}
			};

			// Create an instance of the card Element.
			var card = elements.create('card', {hidePostalCode: false, style: style});
				card.mount('#card-element');
				card.addEventListener('change', function(event) {
					var displayError = document.getElementById('card-errors');
					if (event.error) {
						displayError.textContent = event.error.message;
					} else {
						displayError.textContent = '';
					}
					if (event.brand) {
						jQuery('input[name="CREDITCARDTYPE"]').filter(function() {
							return jQuery(this).val().toLowerCase().indexOf(event.brand) > -1;
						}).prop('checked', 'checked').attr('checked', 'checked');
					}
				});

			$('.payable-form-btn').on('click', function(event) {
				event.preventDefault();
				$('.payable-form-btn').attr('disabled', 'disabled');
				if (PaymentPluginFormValidation(event) & (!PaymentPluginFormGetTotal() || -1 !== ['PayPal', 'Offline'].indexOf(jQuery('input[name="CREDITCARDTYPE"]:checked').val()))) {
					$('.payable-form-btn').closest('form').submit();
				} else if (PaymentPluginFormValidation(event) & !!PaymentPluginFormGetTotal()) {
					stripe.createToken(card).then(function(result) {
						if (result.error) {
							PaymentPluginFormValidationError(result.error.message);
							$('.payable-form-btn').removeAttr('disabled');
							return false;
						}
						var hiddenInput = document.createElement('input');
							hiddenInput.setAttribute('type', 'hidden');
							hiddenInput.setAttribute('name', 'stripeToken');
							hiddenInput.setAttribute('value', result.token.id);
						$('.payable-form-btn').closest('form').append(hiddenInput);
						$('.payable-form-btn').closest('form').submit();
					});
				} else {
					PaymentPluginFormValidationError();
					$('.payable-form-btn').removeAttr('disabled');
				}
			});
		}
	});
})(jQuery.noConflict());
