link.script:
		function TitaniumLocalize(resourceTypeName, resourceLookupName) {
			var returnVal = resourceLookupName;
			var data = {};
			data.resourceTypeName = resourceTypeName;
			data.resourceLookupName = resourceLookupName;

			$.ajax({
				type: "GET",
				data: data,
				async: false,
				url: "/Localization/GetLocalizedResourceString",
				success: function (localizedString) {
					returnVal = localizedString;
				},
				error: function () {
					alert("Unable to get translation for type: " + resourceTypeName + ", name: " + resourceLookupName);
				}
			});
			return returnVal;
		}

div.script:
	$(".flyoutParent").on("click", function (e) {
		e.stopPropagation();
		e.preventDefault();
		$(".flyoutMenu").not($(this).siblings(".flyoutMenu")).hide();
		$(this).siblings(".flyoutMenu").toggle();

		//Manages the display of the glyphicon-chevron-down class based on the flyout menu display.
		if ($(this).children("span").hasClass("glyphicon-chevron-down")) {
			$(this).children("span").removeClass("glyphicon-chevron-down");
		}
		else {
			$(".flyoutParent").not($(this).siblings(".flyoutParent")).children("span").removeClass("glyphicon-chevron-down");
			$(this).children("span").toggleClass("glyphicon-chevron-down");
		}
	});

	//Closes the opened flyout menu when dept menu button is clicked.
	$('#dept-menu-button').on("click", function (e) {
		if ($('.flyoutMenu').is(':visible')) {
			closeFlyoutMenu();
		}
	});

	//Closes the dept menu/flyout menu when looses focus
	$(document).mouseup(function (e) {
		if (!$("#dept-menu-button").is(e.target) && !$("#dept-menu").is(e.target) && $("#dept-menu").has(e.target).length === 0) {
			closeFlyoutMenu();
			closeDeptMenu();
		}
	});

	//Closes the dept menu/flyout menu on escape.
	$("#dept-menu-button, ul#dept-menu").keydown(
		function (e) {
			if (e.keyCode == 27) {
				if ($('.flyoutMenu').is(':visible')) {
					closeFlyoutMenu();
					document
						.activeElement
						.parentElement
						.parentElement
						.previousElementSibling
						.focus();
				}
				else if ($('ul#dept-menu').is(':visible')) {
					closeDeptMenu();
				}
			}
		});

	//Closes the dept menu when Tab is pressed on the last dept menu item.
	$("ul#dept-menu > li:last-child").on('keydown', function (e) {
		if (e.keyCode == 9 && !e.shiftKey) {
			closeDeptMenu();
		}
	});

	//Closes the dept menu when Shift+Tab is pressed on the first dept menu item.
	$("ul#dept-menu > li:first-child").on('keydown', function (e) {
		if (e.shiftKey && e.keyCode == 9) {
			closeDeptMenu();
		}
	});

	//Closes the flyout menu when Tab is pressed on the last flyout menu item.
	$("ul.flyoutMenu > li:last-child").on('keydown', function (e) {
		if (e.keyCode == 9 && !e.shiftKey) {
			closeFlyoutMenu();
		}
	});

	//Closes the flyout menu when Shift+Tab is pressed on the first flyout menu item.
	$("ul.flyoutMenu > li:first-child").on('keydown', function (e) {
		if (e.shiftKey && e.keyCode == 9) {
			closeFlyoutMenu();
		}
	});

	function closeFlyoutMenu() {
		$(".flyoutParent").children("span").removeClass("glyphicon-chevron-down");
		$('.flyoutMenu').hide();
	}

	function closeDeptMenu() {
		$("#dept-menu-button").attr("aria-expanded", "false");
		$("#dept-menu-button").addClass("collapsed");
		$("#dept-menu").removeClass("in");
		$("#dept-menu").attr("aria-expanded", "false");
	}
