meta.script:
window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());
gtag('config', 'UA-350899-1');

link.script:
$(document).ready(function(){
    $('.sidebar-first').pushpin({
      top: $('.content.wrapper').offset().top + $('.authenticated.wrapper').height() + $('.preheader.wrapper').height() + 100,
      bottom: $(document).height() - ($("footer").height() + $('.sidebar-first').height()),
      offset: $('.authenticated.wrapper').height() + $('.preheader.wrapper').height() + 60,
    });
    tocResize();
    $('.mobile-table-of-contents').pushpin({
      top: $('.content.wrapper').offset().top + $('.authenticated.wrapper').height() + $('.preheader.wrapper').height() + 100,
      bottom: Infinity,
      offset: $('.authenticated.wrapper').height() + $('.preheader.wrapper').height() + 60,
    });
    $(".mobile-table-of-contents select").change(function() {
      window.location = $(this).find("option:selected").val();
      $innerText = $(this).find("option:selected").prop('innerText')
      $(".mobile-table-of-contents select option[value='#!'").prop('innerText',$innerText);
      $(".mobile-table-of-contents select option[value='#!'").prop('selected','selected');
    });
  });
