{        
        jQuery.ajax({
                    type : 'POST',
                    dataType : 'json',
                    url : "https://www.seviersd.org/index.php?option=com_jevents&ttoption=com_jevents&typeaheadtask=gwejson&file=fetchlatestevents&path=module&folder=mod_jevents_latest&token=5cad184e4e01ce5ac2a54ad579cc3116",
