div.script:
$(document).ready(function () {
    $("#c1f63864-a579-463c-984f-9d01530504b4").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

jQuery(document).ready(function () {
    jQuery('#d493be91-0849-47b7-b353-102379880b11').hide();
    jQuery('#d493be91-0849-47b7-b353-102379880b11').listSolrResults({
        currentPagePath: '/content/schools/utva/en/faqs/coronavirus-faqs',
        label: 'Upcoming Events',
        listFrom: 'solr',
        limit: '5',
        listType: 'event',
        listOrder: 'desc',
        stateAbbr: 'UT',
        viewAllText: 'View All',
        viewAllUrl: '/calendar.html',
        viewAllTitle: 'View All',
        uuid: 'd493be91-0849-47b7-b353-102379880b11',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

