function ta_paywallLoaded(){

  var ck = 'loggedIn';
  var prior = Cookies.get(ck);
  if( typeof prior != 'undefined' ){
    prior = eval(prior);
  }
  var current = ta_isUserLoggedIn();
  if( prior != current ){
    Cookies.set(ck, current);
  }

}

