function ta_popupCallback(params){    
        if (params['popup'] == 'paywall'){params['popup'] = 'blocking screen';}

        //Classic GA
        if (typeof _gat != "undefined"){
            var ga_account_id = 'UA-85555179-2';
            var tracker = _gat._createTracker(ga_account_id, 'tec1');
            tracker._trackEvent('Paywall ' + params['popup'], params['action'], params['target']);
        //Universal Analytics
        }else if (typeof ga != "undefined"){
                ga('send', 'event', 'Paywall ' + params['popup'] , params['action'], params['target']);
        }
}
