function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());
gtag('config', 'UA-130108692-1');

    $.csHighContrastBar({
    	"show": false,
        "alternateLogo": {
            "selector": "#gb-logo img", // ID OR CLASS OF DEFAULT LOGO 
            "src": "/cms/lib/VA01818723/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", // ALTERNATE LOGO SRC
