					$("#gb-logo").append("").removeClass("hidden");
                }
			} else {
            	if(!true) {
                	$("#gb-logo").append("
").removeClass("hidden");
                } else {
					$("#gb-logo").append("").removeClass("hidden");
                }
			}

            // REMOVE SITE NAME 
 IF EMPTY
            if($.trim("Stafford County Public Schools") == "") {
            	$("#gb-sitename .sitename-one").remove();
            }

            // REMOVE TAGELINE 
 IF EMPTY
