            $("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines .ui-article-title").each(function() {
                $(this).next(".ui-article-description").andSelf().wrapAll('
');

            });
            $("#sw-content-container4.region.ui-hp div.ui-widget.app.headlines .ui-article-title").each(function() {
                $(this).next(".ui-article-description").andSelf().wrapAll('
');

            });

            $("#hp-content-row-one .ui-article-info.has-thumb .ui-article-title").each(function(){
            	$("> a",this).focus(function(){
            	$(this).parent().css("top","0px");
            	});
            });

            // ADD/REMOVE HOVER CLASS FOR HOVER/FOCUS/TOUCH
			$(document).on("mouseenter focusin touchstart", "#hp-content-row-one .ui-article, #hp-content-row-three .ui-article", function(e) {
            	$(this).addClass("hover");
            }).on("mouseleave focusout touchend", "#hp-content-row-one .ui-article, #hp-content-row-three .ui-article", function(e) {
            	$(this).removeClass("hover");
            });
        },

