$('#social a[href]').each(function(){
  var link = $(this);
  var pointsTo = link.attr('href');
  if( pointsTo.indexOf('website.hosted.civiclive.com') > 0 || pointsTo.charAt(0) == '/' || pointsTo.charAt(0) == '#' ){
    // do nothing. this is an internal link
  } else {
    link.attr('target','_blank');
  }
});
